// src/components/Login.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);

  // Toggle theme and persist
  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  // On load, check for saved theme
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "light") setIsDarkMode(false);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);
    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password }
      );
      const { token, user } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);
      setSuccess("Login successful! Redirecting...");
      setTimeout(() => {
        onLogin(user);
        setIsLoading(false);
        window.location.href = "/connect";
      }, 1500);
    } catch (err) {
      setError("Invalid username or password");
      setIsLoading(false);
    }
  };

  return (
    <div className={`login-wrapper ${isDarkMode ? "dark" : "light"}`}>
      {/* Background Effects */}
      <div className="background-effect">
        <div className="particle particle1"></div>
        <div className="particle particle2"></div>
        <div className="particle particle3"></div>
        <div className="particle particle4"></div>
        <div className="particle particle5"></div>
        <div className="particle particle6"></div>
      </div>

      {/* Theme Toggle */}
      <button className="theme-toggle" onClick={toggleTheme}>
        {isDarkMode ? "‚òÄÔ∏è" : "üåô"}
      </button>

      {/* Main Container */}
      <div className="login-container">
        <div className="login-left">
          <div className="brand-section">
            <img src="/logo1922.png" alt="CraftingBrain Logo" className="brand-logo-large" />
            <h1 className="brand-title">CRAFTINGBRAIN</h1>
            <p className="brand-subtitle">Presenting</p>
          </div>
          
          <h2 className="marketing-headline">
            INDIA'S FAVORITE<br/>
            <span className="highlight">MARKETING BOT</span><br/>
            for accelerated business growth
          </h2>
          
          <p className="marketing-description">
            Transform your social media presence with AI-powered content generation. 
            Join 1000+ businesses automating their marketing success.
          </p>
          
          <div className="feature-list">
            <div className="feature-item">
              <div className="feature-icon">üé®</div>
              <div>
                <strong>AI Content Creation</strong>
                <p>Generate stunning visuals instantly</p>
              </div>
            </div>
            <div className="feature-item">
              <div className="feature-icon">üì±</div>
              <div>
                <strong>Multi-Platform Posting</strong>
                <p>Instagram, LinkedIn & more</p>
              </div>
            </div>
            <div className="feature-item">
              <div className="feature-icon">‚ö°</div>
              <div>
                <strong>Smart Automation</strong>
                <p>Schedule & optimize posting times</p>
              </div>
            </div>
          </div>
        </div>

        <div className="login-right">
          <div className="login-card">
            <div className="card-header">
              <h3>Welcome Back</h3>
              <p>Sign in to continue to your dashboard</p>
            </div>

            <form onSubmit={handleSubmit} className="login-form">
              <div className="input-group">
                <label>Username</label>
                <input
                  type="text"
                  placeholder="Enter your username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                  autoComplete="username"
                />
              </div>

              <div className="input-group">
                <label>Password</label>
                <input
                  type="password"
                  placeholder="Enter your password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  autoComplete="current-password"
                />
              </div>

              {error && (
                <div className="alert alert-error">
                  <span className="alert-icon">‚ö†Ô∏è</span>
                  {error}
                </div>
              )}
              
              {success && (
                <div className="alert alert-success">
                  <span className="alert-icon">‚úì</span>
                  {success}
                </div>
              )}

              <button type="submit" className="submit-btn" disabled={isLoading}>
                {isLoading ? (
                  <div className="loader"></div>
                ) : (
                  "Sign In"
                )}
              </button>

              <div className="form-footer">
                <p>Don't have an account? <a href="/register">Sign up</a></p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
