import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';

import Login from './Login';
import ContentCreation from '.co/ContentCreation';
import Register from './Register';
import Survey from './Survey';
import Connect from './Connect';           // ✅ new connect page route

const App = () => {
  const [user, setUser] = useState(null);

  // Check if the user is already logged in (e.g., token exists in localStorage)
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // If you want, you can verify/refresh with backend; for now assume valid:
      const storedUsername = localStorage.getItem('username') || 'admin';
      setUser({ username: storedUsername });
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');   // ✅ clear username as well
    setUser(null);
  };

  return (
    <Router>
      <Routes>
        {/* Login */}
        <Route
          path="/login"
          element={
            user ? <Navigate to="/content" /> : <Login onLogin={handleLogin} />
          }
        />

        {/* Register */}
        <Route
          path="/register"
          element={user ? <Navigate to="/content" /> : <Register />}
        />

        {/* Survey */}
        <Route path="/survey" element={<Survey />} />

        {/* ✅ Connect (shown right after login to link LinkedIn/Instagram) */}
        <Route
          path="/connect"
          element={
            user ? (
              <Connect />
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        {/* Content page (generate & post) */}
        <Route
          path="/content"
          element={
            user ? (
              <div>
                <div style={{ position: 'absolute', top: 10, right: 10 }}>
                  <button
                    onClick={handleLogout}
                    style={{
                      padding: '0.5rem 1rem',
                      backgroundColor: '#facc15',
                      color: '#1f2937',
                      border: 'none',
                      borderRadius: '0.5rem',
                      cursor: 'pointer',
                      fontWeight: '600',
                    }}
                  >
                    Logout
                  </button>
                </div>
                <ContentCreation user={user} />
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        {/* Default */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
