// src/components/LinkedInConnect.js
import React, { useEffect, useState } from "react";
import axios from "axios";

// Hardcoded like your ContentCreation style
const API_LINKEDIN_START = "http://13.233.45.167:5000/oauth/linkedin/start";

export default function LinkedInConnect({ appUser, onConnected }) {
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState("");

  const start = async () => {
    try {
      setErr("");
      setBusy(true);
      const res = await axios.get(API_LINKEDIN_START, { params: { app_user: appUser } });
      window.location.href = res.data.auth_url; // LinkedIn consent screen
    } catch (e) {
      setErr(e?.response?.data?.message || "Failed to open LinkedIn auth.");
      setBusy(false);
    }
  };

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    if (params.get("linkedin") === "connected") {
      onConnected && onConnected();
      window.history.replaceState({}, document.title, window.location.pathname);
      setBusy(false);
    }
  }, [onConnected]);

  return (
    <div>
      <button className="select-all-button" disabled={busy} onClick={start}>
        {busy ? "Opening…" : "Connect LinkedIn"}
      </button>
      {err && <p style={{ color: "tomato", marginTop: 8 }}>{err}</p>}
    </div>
  );
}
