//src/components/Register.js - Enhanced with Marketing Section
import React, { useState, useEffect } from 'react';
import './Register.css';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
    confirmPassword: ''
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem('theme');
    if (saved === 'dark') setIsDarkMode(true);
  }, []);

  useEffect(() => {
    if (formData.password) {
      calculatePasswordStrength(formData.password);
    }
  }, [formData.password]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
  };

  const calculatePasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 6) strength++;
    if (password.length >= 10) strength++;
    if (/[a-z]/.test(password) && /[A-Z]/.test(password)) strength++;
    if (/\d/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;
    setPasswordStrength(strength);
  };

  const getPasswordStrengthLabel = () => {
    if (passwordStrength === 0) return '';
    if (passwordStrength <= 2) return 'Weak';
    if (passwordStrength <= 3) return 'Fair';
    if (passwordStrength <= 4) return 'Good';
    return 'Strong';
  };

  const getPasswordStrengthColor = () => {
    if (passwordStrength <= 2) return '#ef4444';
    if (passwordStrength <= 3) return '#f59e0b';
    if (passwordStrength <= 4) return '#10b981';
    return '#22d3ee';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
    
    if (error) setError('');
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = 'Name is required';
    } else if (formData.name.trim().length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      errors.email = 'Please enter a valid email address';
    }

    if (!formData.username.trim()) {
      errors.username = 'Username is required';
    } else if (formData.username.trim().length < 3) {
      errors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      errors.username = 'Username can only contain letters, numbers, and underscores';
    }

    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }

    setValidationErrors({});
    setIsLoading(true);

    try {
      const response = await fetch(
        'https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/register',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: formData.name.trim(),
            email: formData.email.toLowerCase().trim(),
            username: formData.username.trim(),
            password: formData.password,
            confirmPassword: formData.confirmPassword 
          }),
        }
      );

      const data = await response.json();

      if (response.ok) {
        setSuccess('Registration successful! Taking you to the survey...');
        
        if (data.userId) {
          localStorage.setItem('registeredUserId', data.userId);
        }
        
        setFormData({
          name: '',
          email: '',
          username: '',
          password: '',
          confirmPassword: ''
        });
        
        setTimeout(() => {
          navigate('/survey');
        }, 1500);

      } else {
        if (response.status === 400 || response.status === 409) {
          if (data.error) {
            const errorMessage = data.error.toLowerCase();
            
            if (errorMessage.includes('username') && errorMessage.includes('already')) {
              setValidationErrors({ 
                username: 'This username is already taken. Please choose another one.' 
              });
            } else if (errorMessage.includes('email') && errorMessage.includes('already')) {
              setValidationErrors({ 
                email: 'This email is already registered. Please use a different email or try logging in.' 
              });
            } else if (errorMessage.includes('user') && errorMessage.includes('already')) {
              setError('An account with these details already exists. Please try logging in instead.');
            } else {
              setError(data.error);
            }
          } else {
            setError('User already exists. Please try logging in or use different credentials.');
          }
        } else if (response.status === 500) {
          setError('Server error occurred. Please try again later.');
        } else {
          setError(data.error || 'Registration failed. Please try again.');
        }
      }
    } catch (err) {
      setError('Network error. Please check your connection and try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={`register-container-split ${isDarkMode ? 'dark' : 'light'}`}>
      <button className="theme-toggle top-left" onClick={toggleTheme}>
        {isDarkMode ? '‚òÄÔ∏è Light' : 'üåô Dark'}
      </button>
      <button className="logout-button top-right" onClick={() => navigate('/login')}>
        Back to Login
      </button>

      <div className="extra-icons">
        <span className="icon1">üöÄ</span>
        <span className="icon2">ü§ñ</span>
        <span className="icon3">üìä</span>
        <span className="icon4">üí°</span>
      </div>

      <div className="register-split-content">
        {/* Left Side - Marketing Message */}
        <div className="marketing-section">
          <div className="marketing-content">
            <div className="brand-section">
              <img src="/123" alt="Marketing Bot Logo" className="marketing-logo" />
            </div>
            
            <h1 className="marketing-title">
              GROW YOUR BUSINESS
              <span className="marketing-highlight">10X FASTER</span>
              with AI-Powered Automation
            </h1>
            
            <p className="marketing-description">
              Join 1000+ businesses already using our Marketing Bot to dominate social media. 
              Generate stunning content in seconds, schedule posts automatically, and watch your 
              brand grow while you focus on what matters most.
            </p>
            
            <div className="marketing-stats">
              <div className="stat-item">
                <div className="stat-number">1000+</div>
                <div className="stat-label">Active Users</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">50K+</div>
                <div className="stat-label">Posts Generated</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">95%</div>
                <div className="stat-label">Time Saved</div>
              </div>
            </div>

            <div className="marketing-benefits">
              <div className="benefit-item">
                <span className="benefit-icon">‚ú®</span>
                <span className="benefit-text">AI creates professional content in your brand voice</span>
              </div>
              <div className="benefit-item">
                <span className="benefit-icon">‚ö°</span>
                <span className="benefit-text">Auto-schedule posts for maximum engagement</span>
              </div>
              <div className="benefit-item">
                <span className="benefit-icon">üìà</span>
                <span className="benefit-text">Grow followers & boost conversions effortlessly</span>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Registration Form */}
        <div className="register-section">
          <div className="register-form-container">
            <h1 className="register-form-title">Create Your Account</h1>
            <p className="register-form-subtitle">Start your free journey today</p>

            <div className="info-card">
              <span style={{ fontSize: '1.25rem' }}>‚ÑπÔ∏è</span>
              <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', margin: 0, lineHeight: 1.6 }}>
                After registration, complete a quick survey to personalize your content.
              </p>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Full Name *</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter your full name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={validationErrors.name ? 'error' : ''}
                  required
                />
                {validationErrors.name && <span className="field-error">{validationErrors.name}</span>}
              </div>

              <div className="form-group">
                <label>Email Address *</label>
                <input
                  type="email"
                  name="email"
                  placeholder="your.email@example.com"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={validationErrors.email ? 'error' : ''}
                  required
                />
                {validationErrors.email && <span className="field-error">{validationErrors.email}</span>}
              </div>

              <div className="form-group">
                <label>Username *</label>
                <input
                  type="text"
                  name="username"
                  placeholder="Choose a unique username"
                  value={formData.username}
                  onChange={handleInputChange}
                  className={validationErrors.username ? 'error' : ''}
                  required
                />
                {validationErrors.username && <span className="field-error">{validationErrors.username}</span>}
                <small className="field-hint">Letters, numbers, and underscores only. Min 3 characters.</small>
              </div>

              <div className="form-group">
                <label>Password *</label>
                <input
                  type="password"
                  name="password"
                  placeholder="Create a strong password"
                  value={formData.password}
                  onChange={handleInputChange}
                  className={validationErrors.password ? 'error' : ''}
                  required
                />
                {formData.password && (
                  <div style={{
                    marginTop: '0.5rem',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem'
                  }}>
                    <div style={{
                      flex: 1,
                      height: '4px',
                      background: 'rgba(30, 41, 59, 0.5)',
                      borderRadius: '2px',
                      overflow: 'hidden'
                    }}>
                      <div style={{
                        height: '100%',
                        width: `${(passwordStrength / 5) * 100}%`,
                        background: getPasswordStrengthColor(),
                        transition: 'all 0.3s ease'
                      }}></div>
                    </div>
                    <span style={{
                      fontSize: '0.75rem',
                      color: getPasswordStrengthColor(),
                      fontWeight: 600
                    }}>{getPasswordStrengthLabel()}</span>
                  </div>
                )}
                {validationErrors.password && <span className="field-error">{validationErrors.password}</span>}
                <small className="field-hint">Use uppercase, lowercase, numbers & symbols for better security.</small>
              </div>

              <div className="form-group">
                <label>Confirm Password *</label>
                <input
                  type="password"
                  name="confirmPassword"
                  placeholder="Confirm your password"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  className={validationErrors.confirmPassword ? 'error' : ''}
                  required
                />
                {validationErrors.confirmPassword && <span className="field-error">{validationErrors.confirmPassword}</span>}
              </div>

              {error && (
                <div className="response-error">
                  <div className="response-icon">!</div>
                  <div className="response-content">
                    <p>{error}</p>
                    {error.includes('already') && (
                      <p style={{ marginTop: '0.5rem', fontSize: '0.9em' }}>
                        <a 
                          href="/login" 
                          style={{ color: '#facc15', textDecoration: 'underline' }}
                        >
                          Click here to login instead
                        </a>
                      </p>
                    )}
                  </div>
                </div>
              )}
              
              {success && (
                <div className="response-success">
                  <div className="response-icon">‚úì</div>
                  <div className="response-content">
                    <p>{success}</p>
                  </div>
                </div>
              )}

              <button type="submit" className="post-button" disabled={isLoading}>
                {isLoading ? <span className="spinner"></span> : 'Create Account'}
              </button>
            </form>

            <p style={{ marginTop: '1.5rem', textAlign: 'center', color: '#22d3ee', fontSize: '0.875rem' }}>
              Already have an account?{' '}
              <a href="/login" style={{ color: '#facc15', fontWeight: 'bold', textDecoration: 'none' }}>
                Login here
              </a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
