import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Register = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/register',
        { username, password }
      );
      setMessage('✅ Registration successful! Please login.');
    } catch (err) {
      setMessage('❌ Registration failed. Try a different username.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1 className="login-header">Register</h1>
        <form onSubmit={handleRegister}>
          <div className="form-group">
            <label>Username</label>
            <input value={username} onChange={(e) => setUsername(e.target.value)} required />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          <button type="submit" className="post-button">Register</button>
          {message && <p style={{ marginTop: '1rem', color: 'white' }}>{message}</p>}
        </form>
      </div>
    </div>
  );
};

export default Register;
