// src/components/Connect.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './components/Connect.css';

const Connect = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [user, setUser] = useState(null);
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem('theme');
    if (saved === 'dark') {
      setIsDarkMode(true);
      document.body.classList.add('dark');
    }

    // Get token and check authentication
    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');
    
    if (!token) {
      navigate('/login');
      return;
    }

    // Fetch user details from API/DynamoDB
    const fetchUserData = async () => {
      try {
        const response = await fetch(
          `https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/profile`,
          {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          }
        );

        if (response.ok) {
          const userData = await response.json();
          setUser({
            username: userData.username || username,
            email: userData.email || 'user@example.com',
            businessType: userData.businessType || 'Not specified',
            joinDate: userData.createdAt 
              ? new Date(userData.createdAt).toLocaleDateString('en-US', { month: 'long', year: 'numeric' })
              : 'Recently',
            postsCreated: userData.postsCreated || 0,
            connectedAccounts: userData.connectedAccounts || 0
          });
        } else {
          // Fallback to localStorage data
          setUser({
            username: username || 'User',
            email: 'user@example.com',
            businessType: 'Not specified',
            joinDate: 'Recently',
            postsCreated: 0,
            connectedAccounts: 0
          });
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        // Fallback to localStorage data
        setUser({
          username: username || 'User',
          email: 'user@example.com',
          businessType: 'Not specified',
          joinDate: 'Recently',
          postsCreated: 0,
          connectedAccounts: 0
        });
      }
    };

    fetchUserData();
  }, [navigate]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    
    if (newMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    navigate('/login');
  };

  const handleLinkedInConnect = () => {
    const clientId = '786b6xeqo1bp5e'; // Your LinkedIn Client ID
    const redirectUri = 'http://marketing-bot-frontend.s3-website.ap-south-1.amazonaws.com/login';
    const state = Math.random().toString(36).substring(7);
    const scope = 'openid profile email w_member_social';
    
    const linkedInAuthUrl = `https://www.linkedin.com/oauth/v2/authorization?response_type=code&client_id=${clientId}&redirect_uri=${encodeURIComponent(redirectUri)}&state=${state}&scope=${encodeURIComponent(scope)}`;
    
    window.open(linkedInAuthUrl, '_blank', 'width=600,height=700');
  };

  const handleSocialConnect = (platform) => {
    if (platform === 'LinkedIn') {
      handleLinkedInConnect();
    } else {
      alert(`${platform} connection coming soon!`);
    }
  };

  const connectedAccounts = [
    { name: 'Instagram', icon: 'üì∑', connected: false, color: '#E1306C' },
    { name: 'LinkedIn', icon: 'üíº', connected: false, color: '#0077B5' },
    { name: 'Twitter', icon: 'üê¶', connected: false, color: '#1DA1F2' },
    { name: 'Facebook', icon: 'üë•', connected: false, color: '#1877F2' }
  ];

  const quickActions = [
    { title: 'Create Content', icon: '‚ú®', path: '/content-creation', desc: 'Generate AI-powered posts' },
    { title: 'Schedule Posts', icon: 'üìÖ', path: '/schedule', desc: 'Plan your content calendar' },
    { title: 'Analytics', icon: 'üìä', path: '/analytics', desc: 'Track your performance' },
    { title: 'My Posts', icon: 'üìù', path: '/my-posts', desc: 'View all your content' }
  ];

  return (
    <div className={`connect-container ${isDarkMode ? 'dark' : 'light'}`}>
      {/* Header */}
      <header className="connect-header">
        <div className="header-left">
          <img src="/123.jpg" alt="Logo" className="header-logo" />
          <h1 className="header-title">Marketing Bot</h1>
        </div>
        
        <div className="header-right">
          <button className="theme-toggle-btn" onClick={toggleTheme}>
            {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
          
          <div className="profile-dropdown">
            <button 
              className="profile-btn" 
              onClick={() => setShowProfileMenu(!showProfileMenu)}
            >
              <div className="profile-avatar">
                {user?.username.charAt(0).toUpperCase()}
              </div>
              <span className="profile-name">{user?.username}</span>
              <span className="dropdown-arrow">‚ñº</span>
            </button>
            
            {showProfileMenu && (
              <div className="profile-menu">
                <div className="profile-menu-header">
                  <div className="profile-avatar-large">
                    {user?.username.charAt(0).toUpperCase()}
                  </div>
                  <div className="profile-info">
                    <h3>{user?.username}</h3>
                    <p>{user?.email}</p>
                  </div>
                </div>
                <div className="profile-menu-divider"></div>
                <button className="profile-menu-item" onClick={() => navigate('/profile')}>
                  üë§ View Profile
                </button>
                <button className="profile-menu-item" onClick={() => navigate('/settings')}>
                  ‚öôÔ∏è Settings
                </button>
                <div className="profile-menu-divider"></div>
                <button className="profile-menu-item logout" onClick={handleLogout}>
                  üö™ Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="connect-main">
        <div className="connect-content">
          
          {/* Welcome Section */}
          <section className="welcome-section">
            <h2 className="section-title">Welcome back, {user?.username}! üëã</h2>
            <p className="section-subtitle">
              Let's create amazing content for your {user?.businessType} business
            </p>
          </section>

          {/* Quick Actions */}
          <section className="quick-actions-section">
            <h3 className="section-heading">Quick Actions</h3>
            <div className="quick-actions-grid">
              {quickActions.map((action, index) => (
                <button
                  key={index}
                  className="action-card"
                  onClick={() => navigate(action.path)}
                >
                  <div className="action-icon">{action.icon}</div>
                  <h4 className="action-title">{action.title}</h4>
                  <p className="action-desc">{action.desc}</p>
                </button>
              ))}
            </div>
          </section>

          {/* Social Media Connections */}
          <section className="social-connect-section">
            <div className="section-header">
              <h3 className="section-heading">Connect Social Media</h3>
              <p className="section-description">
                Link your accounts to start posting automatically
              </p>
            </div>
            
            <div className="social-accounts-grid">
              {connectedAccounts.map((account, index) => (
                <div key={index} className="social-account-card">
                  <div className="social-icon" style={{ background: account.color }}>
                    {account.icon}
                  </div>
                  <div className="social-info">
                    <h4 className="social-name">{account.name}</h4>
                    <p className="social-status">
                      {account.connected ? 'Connected' : 'Not connected'}
                    </p>
                  </div>
                  <button 
                    className={`social-connect-btn ${account.connected ? 'connected' : ''}`}
                    onClick={() => handleSocialConnect(account.name)}
                  >
                    {account.connected ? 'Disconnect' : 'Connect'}
                  </button>
                </div>
              ))}
            </div>
          </section>

          {/* User Profile Card */}
          <section className="profile-card-section">
            <div className="profile-card">
              <div className="profile-card-header">
                <div className="profile-avatar-xl">
                  {user?.username.charAt(0).toUpperCase()}
                </div>
                <div className="profile-card-info">
                  <h3 className="profile-card-name">{user?.username}</h3>
                  <p className="profile-card-email">{user?.email}</p>
                </div>
                <button 
                  className="edit-profile-btn"
                  onClick={() => navigate('/profile')}
                >
                  Edit Profile
                </button>
              </div>
              
              <div className="profile-card-divider"></div>
              
              <div className="profile-card-details">
                <div className="profile-detail-item">
                  <span className="detail-label">Business Type</span>
                  <span className="detail-value">{user?.businessType || 'Not specified'}</span>
                </div>
                <div className="profile-detail-item">
                  <span className="detail-label">Member Since</span>
                  <span className="detail-value">{user?.joinDate}</span>
                </div>
                <div className="profile-detail-item">
                  <span className="detail-label">Posts Created</span>
                  <span className="detail-value">{user?.postsCreated || 0}</span>
                </div>
                <div className="profile-detail-item">
                  <span className="detail-label">Accounts Connected</span>
                  <span className="detail-value">{user?.connectedAccounts || 0}/4</span>
                </div>
              </div>
            </div>
          </section>

        </div>
      </main>
    </div>
  );
};

export default Connect;
