// 📁 src/components/Header.js
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Header.css';

const Header = ({ user, onLogout, isDarkMode, onToggleTheme }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleNavigation = (path) => {
    navigate(path);
    setIsMenuOpen(false);
  };

  const handleLogout = () => {
    onLogout();
    setIsMenuOpen(false);
  };

  // Don't show header on login and register pages
  if (location.pathname === '/login' || location.pathname === '/register' || location.pathname === '/survey') {
    return null;
  }

  return (
    <header className={`header ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="header-container">
        <div className="logo-section" onClick={() => handleNavigation('/')}>
          <img src="/logo1922.png" alt="Inikola" className="header-logo" />
          <div className="brand-info">
            <span className="brand-name">INIKOLA</span>
            <span className="brand-tagline">Marketing Bot</span>
          </div>
        </div>

        <nav className="desktop-nav">
          {user && (
            <>
              <button 
                className={`nav-link ${location.pathname === '/connect' ? 'active' : ''}`}
                onClick={() => handleNavigation('/connect')}
              >
                Connect Accounts
              </button>
              <button 
                className={`nav-link ${location.pathname === '/content' ? 'active' : ''}`}
                onClick={() => handleNavigation('/content')}
              >
                Create Content
              </button>
            </>
          )}
        </nav>

        <div className="desktop-actions">
          <button className="theme-toggle" onClick={onToggleTheme}>
            {isDarkMode ? '☀️' : '🌙'}
          </button>
          
          {user ? (
            <div className="user-section">
              <span className="username">Hello, {user.username}</span>
              <button className="logout-btn" onClick={handleLogout}>
                Logout
              </button>
            </div>
          ) : (
            <div className="auth-buttons">
              <button 
                className="auth-btn login-btn"
                onClick={() => handleNavigation('/login')}
              >
                Login
              </button>
              <button 
                className="auth-btn register-btn"
                onClick={() => handleNavigation('/register')}
              >
                Register
              </button>
            </div>
          )}
        </div>

        <div className="mobile-menu">
          <button className="theme-toggle mobile" onClick={onToggleTheme}>
            {isDarkMode ? '☀️' : '🌙'}
          </button>
          
          <button 
            className={`hamburger ${isMenuOpen ? 'active' : ''}`}
            onClick={toggleMenu}
            aria-label="Toggle menu"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>

      {isMenuOpen && (
        <div className="mobile-menu-overlay" onClick={() => setIsMenuOpen(false)}>
          <div className="mobile-menu-content" onClick={(e) => e.stopPropagation()}>
            <div className="mobile-menu-header">
              <div className="mobile-logo-section">
                <img src="/logo1922.png" alt="Inikola" className="mobile-logo" />
                <div>
                  <div className="mobile-brand-name">INIKOLA</div>
                  <div className="mobile-brand-tagline">Marketing Bot</div>
                </div>
              </div>
              <button className="close-btn" onClick={() => setIsMenuOpen(false)}>×</button>
            </div>

            <div className="mobile-menu-items">
              {user ? (
                <>
                  <div className="mobile-user-info">
                    <div className="mobile-avatar">👤</div>
                    <div>
                      <div className="mobile-username">{user.username}</div>
                      <div className="mobile-user-status">Logged in</div>
                    </div>
                  </div>
                  
                  <button 
                    className={`mobile-nav-item ${location.pathname === '/connect' ? 'active' : ''}`}
                    onClick={() => handleNavigation('/connect')}
                  >
                    <span className="nav-icon">🔗</span>
                    Connect Accounts
                  </button>
                  
                  <button 
                    className={`mobile-nav-item ${location.pathname === '/content' ? 'active' : ''}`}
                    onClick={() => handleNavigation('/content')}
                  >
                    <span className="nav-icon">✨</span>
                    Create Content
                  </button>
                  
                  <button className="mobile-nav-item logout" onClick={handleLogout}>
                    <span className="nav-icon">🚪</span>
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <button 
                    className="mobile-nav-item"
                    onClick={() => handleNavigation('/login')}
                  >
                    <span className="nav-icon">🔑</span>
                    Login
                  </button>
                  
                  <button 
                    className="mobile-nav-item"
                    onClick={() => handleNavigation('/register')}
                  >
                    <span className="nav-icon">📝</span>
                    Register
                  </button>
                </>
              )}
            </div>

            <div className="mobile-menu-footer">
              <div className="mobile-theme-section">
                <span>Theme: {isDarkMode ? 'Dark' : 'Light'}</span>
                <button className="mobile-theme-toggle" onClick={onToggleTheme}>
                  {isDarkMode ? '☀️ Switch to Light' : '🌙 Switch to Dark'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
