import React, { useState, useEffect } from 'react';
import './login.css';
const MarketingBotWelcome = () => {
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('theme');
    if (saved) setIsDarkMode(saved === 'dark');
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
  };

  const handleSubmit = () => {
    setError('');
    setSuccess('');
    setIsLoading(true);

    setTimeout(() => {
      if (isLogin) {
        if (username === 'demo' && password === 'demo') {
          setSuccess('Login successful! Redirecting...');
          setTimeout(() => {
            window.location.href = '/dashboard';
          }, 1500);
        } else {
          setError('Invalid credentials. Try demo/demo');
        }
      } else {
        setSuccess('Registration successful! Please login.');
        setTimeout(() => {
          setIsLogin(true);
          setError('');
          setSuccess('');
        }, 2000);
      }
      setIsLoading(false);
    }, 1000);
  };

  return (
    <div className={`min-h-screen transition-all duration-500 ${isDarkMode ? 'bg-gradient-to-br from-gray-900 via-purple-900 to-gray-900' : 'bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50'}`}>
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className={`absolute top-20 left-10 w-72 h-72 rounded-full blur-3xl opacity-20 animate-pulse ${isDarkMode ? 'bg-purple-500' : 'bg-purple-300'}`}></div>
        <div className={`absolute bottom-20 right-10 w-96 h-96 rounded-full blur-3xl opacity-20 animate-pulse ${isDarkMode ? 'bg-blue-500' : 'bg-blue-300'}`} style={{animationDelay: '1s'}}></div>
        <div className={`absolute top-1/2 left-1/2 w-80 h-80 rounded-full blur-3xl opacity-10 animate-pulse ${isDarkMode ? 'bg-pink-500' : 'bg-pink-300'}`} style={{animationDelay: '0.5s'}}></div>
      </div>

      <button
        onClick={toggleTheme}
        className={`fixed top-6 right-6 z-50 p-3 rounded-full backdrop-blur-lg transition-all duration-300 hover:scale-110 ${
          isDarkMode 
            ? 'bg-white/10 text-white hover:bg-white/20' 
            : 'bg-black/10 text-gray-900 hover:bg-black/20'
        }`}
      >
        {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
      </button>

      <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
        <div className="w-full max-w-6xl flex flex-col lg:flex-row items-center gap-12">
          
          <div className="flex-1 text-center lg:text-left space-y-8">
            <div className="flex items-center justify-center lg:justify-start gap-4">
              <div className={`w-16 h-16 rounded-2xl flex items-center justify-center ${
                isDarkMode ? 'bg-gradient-to-br from-purple-500 to-pink-500' : 'bg-gradient-to-br from-purple-600 to-pink-600'
              }`}>
                <span className="text-3xl">ü§ñ</span>
              </div>
              <div>
                <h1 className={`text-4xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                  Marketing Bot
                </h1>
                <p className={`text-sm ${isDarkMode ? 'text-purple-300' : 'text-purple-600'}`}>
                  by INIKOLA
                </p>
              </div>
            </div>

            <div className="space-y-4">
              <h2 className={`text-5xl lg:text-6xl font-bold leading-tight ${
                isDarkMode ? 'text-white' : 'text-gray-900'
              }`}>
                Welcome to
                <span className="block bg-gradient-to-r from-purple-400 via-pink-400 to-blue-400 bg-clip-text text-transparent">
                  AI-Powered Marketing
                </span>
              </h2>
              <p className={`text-xl ${isDarkMode ? 'text-gray-300' : 'text-gray-600'} max-w-2xl`}>
                Transform your social media presence with intelligent automation. Generate stunning content, 
                schedule posts effortlessly, and watch your brand grow across Instagram, LinkedIn, and more.
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 pt-4">
              {[
                { icon: 'üé®', title: 'AI Content Creation', desc: 'Generate professional images & captions', gradient: 'from-purple-500 to-pink-500' },
                { icon: 'üì±', title: 'Multi-Platform', desc: 'Post to Instagram, LinkedIn & more', gradient: 'from-blue-500 to-cyan-500' },
                { icon: '‚ö°', title: 'Smart Scheduling', desc: 'Optimize posting times automatically', gradient: 'from-yellow-500 to-orange-500' },
                { icon: 'üìä', title: 'Analytics', desc: 'Track performance & engagement', gradient: 'from-green-500 to-emerald-500' }
              ].map((feature, idx) => (
                <div 
                  key={idx}
                  className={`group p-4 rounded-2xl backdrop-blur-lg transition-all duration-300 hover:scale-105 ${
                    isDarkMode 
                      ? 'bg-white/5 hover:bg-white/10 border border-white/10' 
                      : 'bg-white/60 hover:bg-white/80 border border-black/5'
                  }`}
                >
                  <div className={`w-12 h-12 rounded-xl bg-gradient-to-br ${feature.gradient} flex items-center justify-center mb-3 group-hover:scale-110 transition-transform duration-300`}>
                    <span className="text-2xl">{feature.icon}</span>
                  </div>
                  <h3 className={`font-semibold mb-1 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    {feature.title}
                  </h3>
                  <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                    {feature.desc}
                  </p>
                </div>
              ))}
            </div>

            <div className={`mt-8 p-6 rounded-2xl backdrop-blur-lg ${
              isDarkMode 
                ? 'bg-gradient-to-r from-purple-500/10 to-pink-500/10 border border-white/10' 
                : 'bg-gradient-to-r from-purple-100 to-pink-100 border border-purple-200'
            }`}>
              <div className="flex items-center gap-4">
                <div className={`text-4xl ${isDarkMode ? 'animate-pulse' : ''}`}>üöÄ</div>
                <div>
                  <h4 className={`font-bold text-lg mb-1 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    Join 1000+ Growing Businesses
                  </h4>
                  <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                    Trusted by startups and enterprises worldwide for marketing automation
                  </p>
                </div>
              </div>
            </div>
          </div>

          <div className={`w-full lg:w-96 p-8 rounded-3xl backdrop-blur-xl shadow-2xl ${
            isDarkMode 
              ? 'bg-white/10 border border-white/20' 
              : 'bg-white/80 border border-black/5'
          }`}>
            <div className="text-center mb-8">
              <h3 className={`text-2xl font-bold mb-2 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                {isLogin ? 'Welcome Back!' : 'Create Account'}
              </h3>
              <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                {isLogin ? 'Sign in to continue to your dashboard' : 'Sign up to get started with Marketing Bot'}
              </p>
            </div>

            <div className="space-y-4">
              {!isLogin && (
                <div>
                  <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                    Email
                  </label>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className={`w-full px-4 py-3 rounded-xl transition-all duration-300 ${
                      isDarkMode 
                        ? 'bg-white/5 border border-white/10 text-white placeholder-gray-500 focus:bg-white/10 focus:border-purple-400' 
                        : 'bg-white border border-gray-200 text-gray-900 placeholder-gray-400 focus:border-purple-500'
                    } focus:outline-none focus:ring-2 focus:ring-purple-400/50`}
                    placeholder="Enter your email"
                  />
                </div>
              )}

              <div>
                <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  Username
                </label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className={`w-full px-4 py-3 rounded-xl transition-all duration-300 ${
                    isDarkMode 
                      ? 'bg-white/5 border border-white/10 text-white placeholder-gray-500 focus:bg-white/10 focus:border-purple-400' 
                      : 'bg-white border border-gray-200 text-gray-900 placeholder-gray-400 focus:border-purple-500'
                  } focus:outline-none focus:ring-2 focus:ring-purple-400/50`}
                  placeholder="Enter your username"
                />
              </div>

              <div>
                <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  Password
                </label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className={`w-full px-4 py-3 rounded-xl transition-all duration-300 ${
                    isDarkMode 
                      ? 'bg-white/5 border border-white/10 text-white placeholder-gray-500 focus:bg-white/10 focus:border-purple-400' 
                      : 'bg-white border border-gray-200 text-gray-900 placeholder-gray-400 focus:border-purple-500'
                  } focus:outline-none focus:ring-2 focus:ring-purple-400/50`}
                  placeholder="Enter your password"
                />
              </div>

              {error && (
                <div className="p-3 rounded-xl bg-red-500/10 border border-red-500/20 text-red-500 text-sm">
                  {error}
                </div>
              )}

              {success && (
                <div className="p-3 rounded-xl bg-green-500/10 border border-green-500/20 text-green-500 text-sm">
                  {success}
                </div>
              )}

              <button
                onClick={handleSubmit}
                disabled={isLoading}
                className="w-full py-3 rounded-xl font-semibold text-white bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 transition-all duration-300 hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg hover:shadow-xl"
              >
                {isLoading ? (
                  <div className="flex items-center justify-center gap-2">
                    <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                    <span>Processing...</span>
                  </div>
                ) : (
                  isLogin ? 'Sign In' : 'Create Account'
                )}
              </button>
            </div>

            <div className={`mt-6 text-center text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
              {isLogin ? "Don't have an account? " : "Already have an account? "}
              <button
                onClick={() => {
                  setIsLogin(!isLogin);
                  setError('');
                  setSuccess('');
                }}
                className="font-semibold text-purple-400 hover:text-purple-300 transition-colors"
              >
                {isLogin ? 'Sign Up' : 'Sign In'}
              </button>
            </div>

            <div className="mt-6">
              <div className={`text-center text-sm mb-4 ${isDarkMode ? 'text-gray-500' : 'text-gray-600'}`}>
                Or continue with
              </div>
              <div className="flex gap-3">
                <button className={`flex-1 py-3 rounded-xl font-medium transition-all duration-300 hover:scale-105 flex items-center justify-center gap-2 ${
                  isDarkMode 
                    ? 'bg-white/5 hover:bg-white/10 text-white border border-white/10' 
                    : 'bg-white hover:bg-gray-50 text-gray-900 border border-gray-200'
                }`}>
                  <svg className="w-5 h-5" viewBox="0 0 24 24" fill="currentColor">
                    <path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" fill="#4285F4"/>
                    <path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" fill="#34A853"/>
                    <path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" fill="#FBBC05"/>
                    <path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" fill="#EA4335"/>
                  </svg>
                  <span>Google</span>
                </button>
                <button className={`flex-1 py-3 rounded-xl font-medium transition-all duration-300 hover:scale-105 flex items-center justify-center gap-2 ${
                  isDarkMode 
                    ? 'bg-white/5 hover:bg-white/10 text-white border border-white/10' 
                    : 'bg-white hover:bg-gray-50 text-gray-900 border border-gray-200'
                }`}>
                  <svg className="w-5 h-5" viewBox="0 0 24 24" fill="#0077B5">
                    <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
                  </svg>
                  <span>LinkedIn</span>
                </button>
              </div>
            </div>

            <div className={`mt-6 pt-6 border-t ${isDarkMode ? 'border-white/10' : 'border-gray-200'}`}>
              <div className="flex items-center justify-center gap-6">
                <div className="text-center">
                  <div className={`text-2xl font-bold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent`}>
                    1000+
                  </div>
                  <div className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-600'}`}>Active Users</div>
                </div>
                <div className={`h-8 w-px ${isDarkMode ? 'bg-white/10' : 'bg-gray-200'}`}></div>
                <div className="text-center">
                  <div className={`text-2xl font-bold bg-gradient-to-r from-blue-400 to-cyan-400 bg-clip-text text-transparent`}>
                    50K+
                  </div>
                  <div className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-600'}`}>Posts Created</div>
                </div>
                <div className={`h-8 w-px ${isDarkMode ? 'bg-white/10' : 'bg-gray-200'}`}></div>
                <div className="text-center">
                  <div className={`text-2xl font-bold bg-gradient-to-r from-green-400 to-emerald-400 bg-clip-text text-transparent`}>
                    98%
                  </div>
                  <div className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-600'}`}>Satisfaction</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MarketingBotWelcome;
