// src/Connect.js
import React, { useState, useEffect, useCallback } from 'react';
import LinkedInConnect from './components/LinkedInConnect';

const ConnectPage = () => {
  const [socialStatus, setSocialStatus] = useState({
    linkedin: { connected: false },
    instagram: { connected: false }
  });
  const [loading, setLoading] = useState(true);
  const [canContinue, setCanContinue] = useState(false);

  const appUser = localStorage.getItem('appUser') || 'defaultUser';

  const checkSocialStatus = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        window.location.href = '/login';
        return;
      }

      const response = await fetch(`http://13.233.45.167:5000/social/status?app_user=${appUser}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.status === 401) {
        const data = await response.json();
        if (data.token_expired) {
          localStorage.removeItem('token');
          alert('Your session has expired. Please login again.');
          window.location.href = '/login';
          return;
        }
      }

      if (response.ok) {
        const data = await response.json();
        setSocialStatus(data);
        
        // Enable continue button if at least one platform is connected
        const hasAnyConnection = data.linkedin.connected || data.instagram.connected;
        setCanContinue(hasAnyConnection);
      }
    } catch (error) {
      console.error('Error checking social status:', error);
    } finally {
      setLoading(false);
    }
  }, [appUser]);

  useEffect(() => {
    checkSocialStatus();
  }, [checkSocialStatus]);

  const handleContinueToContent = () => {
    if (canContinue) {
      window.location.href = '/content';
    } else {
      alert('Please connect at least one social media platform to continue.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('appUser');
    window.location.href = '/login';
  };

  if (loading) {
    return <div style={{ textAlign: 'center', padding: '50px' }}>Loading...</div>;
  }

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
      padding: '20px' 
    }}>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '40px' 
      }}>
        <h1 style={{ color: 'white' }}>CraftingBrain</h1>
        <button 
          onClick={handleLogout} 
          style={{
            background: '#dc3545',
            color: 'white',
            border: 'none',
            padding: '8px 16px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Logout
        </button>
      </div>
      
      <div style={{
        maxWidth: '800px',
        margin: '0 auto',
        background: 'white',
        borderRadius: '12px',
        padding: '40px',
        textAlign: 'center'
      }}>
        <h2>Connect Your Social Accounts</h2>
        <p>Link your profiles for seamless auto-posting</p>
        
        <div style={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr',
          gap: '20px',
          margin: '30px 0'
        }}>
          <div style={{
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <h3>LinkedIn</h3>
            <p>{socialStatus.linkedin.connected ? 'Connected' : 'Not connected'}</p>
            <LinkedInConnect 
              appUser={appUser}
              onConnected={checkSocialStatus}
              connected={socialStatus.linkedin.connected}
            />
          </div>
          
          <div style={{
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <h3>Instagram</h3>
            <p>{socialStatus.instagram.connected ? 'Connected' : 'Not connected'}</p>
            <button 
              style={{
                backgroundColor: '#ccc',
                color: '#666',
                border: 'none',
                padding: '8px 16px',
                borderRadius: '4px',
                cursor: 'not-allowed'
              }}
              disabled
            >
              Connect Instagram
            </button>
          </div>
        </div>
        
        <button 
          style={{
            width: '100%',
            padding: '15px',
            fontSize: '16px',
            fontWeight: 'bold',
            border: 'none',
            borderRadius: '8px',
            margin: '20px 0',
            cursor: canContinue ? 'pointer' : 'not-allowed',
            background: canContinue ? '#f4c542' : '#ccc',
            color: canContinue ? '#333' : '#666'
          }}
          onClick={handleContinueToContent}
          disabled={!canContinue}
        >
          CONTINUE TO CONTENT
        </button>
        
        <button 
          onClick={checkSocialStatus}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          REFRESH STATUS
        </button>
        
        {socialStatus.linkedin.connected && (
          <p style={{
            marginTop: '20px',
            fontSize: '12px',
            color: '#666'
          }}>
            Tip: If your LinkedIn has a Page where you're an admin, we'll auto-select the org URN; else we use your personal URN.
          </p>
        )}
      </div>
    </div>
  );
};

export default ConnectPage;
