import React, { useEffect, useMemo, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import LinkedInConnect from "./components/LinkedInConnect";
import InstagramConnect from "./components/InstagramConnect";
import "./comp/Connect.css";

// Hardcoded endpoint for social status
const API_SOCIAL_STATUS = "http://13.233.45.167:5000/social/status";

export default function Connect() {
  const [status, setStatus] = useState({
    linkedin: { connected: false, detail: null },
    instagram: { connected: false, detail: null },
  });
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [isDarkMode, setIsDarkMode] = useState(false);

  const token = localStorage.getItem("token");
  const appUser = useMemo(() => localStorage.getItem("username") || localStorage.getItem("appUser") || "defaultUser", []);
  const navigate = useNavigate();

  // Check if user is properly logged in
  useEffect(() => {
    console.log("Token:", token);
    console.log("App User:", appUser);
    
    if (!token || token === "null" || token === "undefined") {
      console.log("No valid token found, redirecting to login");
      navigate("/login");
      return;
    }
  }, [token, appUser, navigate]);

  // Load saved theme
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark") setIsDarkMode(true);
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  // Memoize fetchStatus to prevent unnecessary re-renders
  const fetchStatus = useCallback(async () => {
    try {
      setErr("");
      setLoading(true);
      
      const currentToken = localStorage.getItem("token");
      if (!currentToken || currentToken === "null" || currentToken === "undefined") {
        console.log("No valid token during fetch, redirecting to login");
        navigate("/login");
        return;
      }
      
      console.log("Fetching status with token:", currentToken);
      const res = await axios.get(API_SOCIAL_STATUS, {
        headers: { Authorization: `Bearer ${currentToken}` },
        params: { app_user: appUser },
      });
      console.log("API Response:", res.data);
      setStatus({
        linkedin: res.data.linkedin || { connected: false, detail: null },
        instagram: res.data.instagram || { connected: false, detail: null },
      });
    } catch (e) {
      console.error("Fetch status error:", e);
      
      if (e.response?.status === 401) {
        console.log("401 error - token expired or invalid, redirecting to login");
        localStorage.removeItem("token");
        localStorage.removeItem("username");
        localStorage.removeItem("appUser");
        navigate("/login");
        return;
      }
      
      const errorMessage = e?.response?.data?.message || "Could not load connection status.";
      setErr(errorMessage);
      setStatus({
        linkedin: { connected: false, detail: null },
        instagram: { connected: false, detail: null },
      });
    } finally {
      setLoading(false);
      console.log("Loading state set to false");
    }
  }, [appUser, navigate]);

  useEffect(() => {
    if (!appUser) {
      const u = new URLSearchParams(window.location.search).get("u");
      if (u) localStorage.setItem("username", u);
    }
    
    // Only fetch status if we have a valid token
    if (token && token !== "null" && token !== "undefined") {
      fetchStatus();
    }
  }, [appUser, fetchStatus, token]);

  const goToContent = () => {
    console.log("Button clicked - navigating to content creation");
    
    const currentToken = localStorage.getItem("token");
    console.log("Token check:", !!currentToken);
    
    if (!currentToken || currentToken === "null" || currentToken === "undefined") {
      console.warn("No token found, redirecting to login");
      navigate("/login");
      return;
    }
    
    // Always allow navigation to content - no connection requirement
    try {
      console.log("Navigating to /content");
      navigate("/content");
      console.log("Navigation called successfully");
    } catch (error) {
      console.error("Navigation error:", error);
      setErr("Failed to navigate to content page.");
    }
  };

  const handleLogout = () => {
    console.log("Logging out...");
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    localStorage.removeItem("appUser");
    navigate("/login");
  };

  // Don't render anything if no token
  if (!token || token === "null" || token === "undefined") {
    return <div className="loading-redirect">Redirecting to login...</div>;
  }

  const connectedCount = (status.linkedin.connected ? 1 : 0) + (status.instagram.connected ? 1 : 0);

  return (
    <div className={`connect-container ${isDarkMode ? "dark" : "light"}`}>
      {/* Theme Toggle */}
      <button className="theme-toggle" onClick={toggleTheme}>
        {isDarkMode ? "‚òÄÔ∏è Light" : "üåô Dark"}
      </button>

      {/* Logout Button */}
      <button className="logout-button" onClick={handleLogout}>
        üö™ Logout
      </button>

      {/* Floating Background Elements */}
      <div className="floating-elements">
        <div className="floating-icon icon-1">üöÄ</div>
        <div className="floating-icon icon-2">ü§ñ</div>
        <div className="floating-icon icon-3">üìä</div>
        <div className="floating-icon icon-4">üí°</div>
        <div className="floating-icon icon-5">‚ö°</div>
        <div className="floating-icon icon-6">üéØ</div>
      </div>

      <div className="connect-content">
        <div className="connect-header">
          <img src="/logo1922.png" alt="CraftingBrain Logo" className="connect-logo" />
          <h1 className="connect-title">Connect Your Social Media Platforms</h1>
          <p className="connect-subtitle">
            Link your social accounts to enable seamless auto-posting before you proceed to content creation
          </p>
          <div className="connection-progress">
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${(connectedCount / 2) * 100}%` }}
              ></div>
            </div>
            <span className="progress-text">{connectedCount}/2 platforms connected</span>
          </div>
        </div>

        {err && (
          <div className="error-message">
            <div className="error-icon">‚ö†Ô∏è</div>
            <div className="error-content">
              <p>{err}</p>
            </div>
          </div>
        )}

        <div className="platforms-grid">
          <div className={`platform-card ${status.linkedin.connected ? "connected" : "disconnected"}`}>
            <div className="platform-header">
              <div className="platform-icon linkedin-icon">
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
                </svg>
              </div>
              <div className="platform-info">
                <h3>LinkedIn</h3>
                <p className="platform-status">
                  {status.linkedin.connected
                    ? `‚úì Connected (${
                        status.linkedin.detail?.preferred_posting_urn?.includes("organization")
                          ? "Organization"
                          : "Personal"
                      })`
                    : "Not connected"}
                </p>
              </div>
              <div className={`connection-indicator ${status.linkedin.connected ? "connected" : ""}`}>
                {status.linkedin.connected ? "‚úì" : "‚óã"}
              </div>
            </div>
            <div className="platform-features">
              <span className="feature-tag">Professional Network</span>
              <span className="feature-tag">Business Posts</span>
            </div>
            <div className="platform-action">
              <LinkedInConnect
                appUser={appUser}
                onConnected={fetchStatus}
                connected={status.linkedin.connected}
              />
            </div>
          </div>

          <div className={`platform-card ${status.instagram.connected ? "connected" : "disconnected"}`}>
            <div className="platform-header">
              <div className="platform-icon instagram-icon">
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
                </svg>
              </div>
              <div className="platform-info">
                <h3>Instagram</h3>
                <p className="platform-status">
                  {status.instagram.connected
                    ? "‚úì Connected (Business/Creator)"
                    : "Not connected"}
                </p>
              </div>
              <div className={`connection-indicator ${status.instagram.connected ? "connected" : ""}`}>
                {status.instagram.connected ? "‚úì" : "‚óã"}
              </div>
            </div>
            <div className="platform-features">
              <span className="feature-tag">Visual Content</span>
              <span className="feature-tag">Stories & Posts</span>
            </div>
            <div className="platform-action">
              <InstagramConnect
                appUser={appUser}
                onConnected={fetchStatus}
                connected={status.instagram.connected}
              />
            </div>
          </div>
        </div>

        <div className="connection-benefits">
          <h3>Why Connect Your Social Platforms?</h3>
          <div className="benefits-grid">
            <div className="benefit-item">
              <span className="benefit-icon">‚ö°</span>
              <span>Auto-posting to multiple platforms</span>
            </div>
            <div className="benefit-item">
              <span className="benefit-icon">üìà</span>
              <span>Consistent content distribution</span>
            </div>
            <div className="benefit-item">
              <span className="benefit-icon">‚è∞</span>
              <span>Save time with batch publishing</span>
            </div>
            <div className="benefit-item">
              <span className="benefit-icon">üéØ</span>
              <span>Reach wider audience instantly</span>
            </div>
          </div>
        </div>

        <div className="action-buttons">
          <button
            className={`continue-button ${connectedCount > 0 ? 'highlighted' : ''}`}
            onClick={goToContent}
            disabled={loading}
          >
            <span className="button-icon">üöÄ</span>
            Continue to Content Creation
            {connectedCount > 0 && <span className="button-badge">{connectedCount}</span>}
          </button>
          
          <button className="refresh-button" onClick={fetchStatus} disabled={loading}>
            <span className="button-icon">üîÑ</span>
            {loading ? 'Checking...' : 'Refresh Status'}
          </button>
        </div>

        <div className="help-text">
          <p>
            <strong>Pro Tip:</strong> Connect at least one platform to enable auto-posting. 
            You can always add more platforms later!
          </p>
          <p className="small-text">
            LinkedIn: If you're an admin of a LinkedIn Page, we'll auto-select the organization URN; 
            otherwise, we'll use your personal profile.
          </p>
        </div>

        {/* Debug info - only show in development */}
        {process.env.NODE_ENV === 'development' && (
          <div className="debug-info">
            Debug: Token exists: {!!token}, AppUser: {appUser}, 
            LinkedIn: {status.linkedin.connected ? "Connected" : "Not connected"},
            Instagram: {status.instagram.connected ? "Connected" : "Not connected"}
          </div>
        )}
      </div>
    </div>
  );
}
