// src/Connect.js
import React, { useState, useEffect, useCallback } from 'react';
import LinkedInConnect from './components/LinkedInConnect';

const ConnectPage = () => {
  const [socialStatus, setSocialStatus] = useState({
    linkedin: { connected: false },
    instagram: { connected: false }
  });
  const [loading, setLoading] = useState(true);
  const [canContinue, setCanContinue] = useState(false);

  const appUser = localStorage.getItem('appUser') || 'defaultUser';

  const checkSocialStatus = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      
      // Debug: Check if token exists
      console.log('Token from localStorage:', token);
      
      if (!token || token === 'null' || token === 'undefined') {
        console.log('No valid token found, redirecting to login');
        alert('Please login first to access this page.');
        window.location.href = '/login';
        return;
      }

      const response = await fetch(`http://13.233.45.167:5000/social/status?app_user=${appUser}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('Social status response status:', response.status);

      if (response.status === 401) {
        const data = await response.json();
        console.log('401 response data:', data);
        
        if (data.token_expired || data.error.includes('expired')) {
          localStorage.removeItem('token');
          localStorage.removeItem('appUser');
          alert('Your session has expired. Please login again.');
          window.location.href = '/login';
          return;
        }
      }

      if (response.ok) {
        const data = await response.json();
        console.log('Social status data:', data);
        setSocialStatus(data);
        
        // Enable continue button if at least one platform is connected
        const hasAnyConnection = data.linkedin.connected || data.instagram.connected;
        setCanContinue(hasAnyConnection);
        
        console.log('Can continue:', hasAnyConnection);
      } else {
        console.error('Failed to fetch social status:', response.status, response.statusText);
        const errorData = await response.text();
        console.error('Error response:', errorData);
      }
    } catch (error) {
      console.error('Error checking social status:', error);
      alert('Error checking connection status. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [appUser]);

  useEffect(() => {
    // Check if user is logged in first
    const token = localStorage.getItem('token');
    if (!token || token === 'null' || token === 'undefined') {
      alert('Please login to access this page.');
      window.location.href = '/login';
      return;
    }
    
    checkSocialStatus();
  }, [checkSocialStatus]);

  const handleContinueToContent = () => {
    if (canContinue) {
      console.log('Redirecting to content page...');
      window.location.href = '/content';
    } else {
      alert('Please connect at least one social media platform to continue.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('appUser');
    console.log('Logged out, redirecting to login');
    window.location.href = '/login';
  };

  // Show different loading message while checking token
  if (loading) {
    return (
      <div style={{ 
        textAlign: 'center', 
        padding: '50px',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div>
          <h3>Checking authentication...</h3>
          <p>Please wait while we verify your login status.</p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
      padding: '20px' 
    }}>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '40px' 
      }}>
        <h1 style={{ color: 'white' }}>CraftingBrain</h1>
        <button 
          onClick={handleLogout} 
          style={{
            background: '#dc3545',
            color: 'white',
            border: 'none',
            padding: '8px 16px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Logout
        </button>
      </div>
      
      <div style={{
        maxWidth: '800px',
        margin: '0 auto',
        background: 'white',
        borderRadius: '12px',
        padding: '40px',
        textAlign: 'center'
      }}>
        <h2>Connect Your Social Accounts</h2>
        <p>Link your profiles for seamless auto-posting</p>
        
        <div style={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr',
          gap: '20px',
          margin: '30px 0'
        }}>
          <div style={{
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <h3>LinkedIn</h3>
            <p style={{ 
              color: socialStatus.linkedin.connected ? 'green' : 'red',
              fontWeight: 'bold'
            }}>
              {socialStatus.linkedin.connected ? 'Connected ✓' : 'Not connected'}
            </p>
            <LinkedInConnect 
              appUser={appUser}
              onConnected={checkSocialStatus}
              connected={socialStatus.linkedin.connected}
            />
          </div>
          
          <div style={{
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <h3>Instagram</h3>
            <p style={{ 
              color: socialStatus.instagram.connected ? 'green' : 'red',
              fontWeight: 'bold'
            }}>
              {socialStatus.instagram.connected ? 'Connected ✓' : 'Not connected'}
            </p>
            <button 
              style={{
                backgroundColor: '#ccc',
                color: '#666',
                border: 'none',
                padding: '8px 16px',
                borderRadius: '4px',
                cursor: 'not-allowed'
              }}
              disabled
            >
              Connect Instagram (Coming Soon)
            </button>
          </div>
        </div>
        
        <button 
          style={{
            width: '100%',
            padding: '15px',
            fontSize: '16px',
            fontWeight: 'bold',
            border: 'none',
            borderRadius: '8px',
            margin: '20px 0',
            cursor: canContinue ? 'pointer' : 'not-allowed',
            background: canContinue ? '#f4c542' : '#ccc',
            color: canContinue ? '#333' : '#666',
            transition: 'all 0.3s ease'
          }}
          onClick={handleContinueToContent}
          disabled={!canContinue}
        >
          {canContinue ? 'CONTINUE TO CONTENT →' : 'CONNECT A PLATFORM TO CONTINUE'}
        </button>
        
        <button 
          onClick={checkSocialStatus}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '4px',
            cursor: 'pointer',
            margin: '10px'
          }}
        >
          REFRESH STATUS
        </button>
        
        {socialStatus.linkedin.connected && (
          <div style={{
            marginTop: '20px',
            padding: '15px',
            backgroundColor: '#e8f5e8',
            borderRadius: '8px',
            border: '1px solid #4caf50'
          }}>
            <p style={{
              fontSize: '14px',
              color: '#2e7d32',
              margin: 0
            }}>
              ✓ LinkedIn connected successfully! 
              {socialStatus.linkedin.detail?.preferred_posting_urn?.includes('organization') 
                ? ' Posting to Organization Page.' 
                : ' Posting to Personal Profile.'}
            </p>
          </div>
        )}
        
        {/* Debug info - remove in production */}
        <div style={{
          marginTop: '20px',
          padding: '10px',
          backgroundColor: '#f8f9fa',
          borderRadius: '4px',
          fontSize: '12px',
          color: '#666'
        }}>
          <p><strong>Debug Info:</strong></p>
          <p>App User: {appUser}</p>
          <p>LinkedIn Connected: {socialStatus.linkedin.connected ? 'Yes' : 'No'}</p>
          <p>Can Continue: {canContinue ? 'Yes' : 'No'}</p>
        </div>
      </div>
    </div>
  );
};

export default ConnectPage;
