//src/app.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Login from "./components/Login";
import ContentCreation from "./components/ContentCreation";
import Register from "./components/Register";
import Survey from "./components/Survey";
import Connect from "./Connect";

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setUser({ username: localStorage.getItem("username") || "admin" });
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            user ? <Navigate to="/connect" /> : <Login onLogin={handleLogin} />
          }
        />
        
        <Route
          path="/register"
          element={user ? <Navigate to="/connect" /> : <Register />}
        />

        <Route path="/survey" element={<Survey />} />

        <Route
          path="/connect"
          element={user ? <Connect /> : <Navigate to="/login" />}
        />

        <Route
          path="/content-creation"
          element={
            user ? <ContentCreation user={user} /> : <Navigate to="/login" />
          }
        />

        <Route 
          path="/schedule" 
          element={
            user ? (
              <div style={{ padding: '2rem' }}>Schedule Page - Coming Soon</div>
            ) : (
              <Navigate to="/login" />
            )
          } 
        />
        
        <Route 
          path="/analytics" 
          element={
            user ? (
              <div style={{ padding: '2rem' }}>Analytics Page - Coming Soon</div>
            ) : (
              <Navigate to="/login" />
            )
          } 
        />
        
        <Route 
          path="/my-posts" 
          element={
            user ? (
              <div style={{ padding: '2rem' }}>My Posts Page - Coming Soon</div>
            ) : (
              <Navigate to="/login" />
            )
          } 
        />
        
        <Route 
          path="/profile" 
          element={
            user ? (
              <div style={{ padding: '2rem' }}>Profile Page - Coming Soon</div>
            ) : (
              <Navigate to="/login" />
            )
          } 
        />
        
        <Route 
          path="/settings" 
          element={
            user ? (
              <div style={{ padding: '2rem' }}>Settings Page - Coming Soon</div>
            ) : (
              <Navigate to="/login" />
            )
          } 
        />

        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
