import React, { useEffect, useMemo, useState, useCallback } from "react";
import axios from "axios";
import LinkedInConnect from "./components/LinkedInConnect";
import InstagramConnect from "./components/InstagramConnect";
import "./components/Login.css";

// Hardcoded endpoint for social status (same style as ContentCreation)
const API_SOCIAL_STATUS = "http://13.233.45.167:5000/social/status";

export default function Connect() {
  const [status, setStatus] = useState({
    linkedin: { connected: false, detail: null },
    instagram: { connected: false, detail: null },
  });
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [isDarkMode, setIsDarkMode] = useState(false);

  const token = localStorage.getItem("token");
  const appUser = useMemo(() => localStorage.getItem("username") || "", []);

  // Load saved theme
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark") setIsDarkMode(true);
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  // Memoize fetchStatus to prevent unnecessary re-renders
  const fetchStatus = useCallback(async () => {
    try {
      setErr("");
      setLoading(true);
      const res = await axios.get(API_SOCIAL_STATUS, {
        headers: { Authorization: `Bearer ${token}` },
        params: { app_user: appUser },
      });
      setStatus({
        linkedin: res.data.linkedin || { connected: false },
        instagram: res.data.instagram || { connected: false },
      });
    } catch (e) {
      setErr(e?.response?.data?.message || "Could not load connection status.");
    } finally {
      setLoading(false);
    }
  }, [token, appUser]); // Dependencies: token and appUser

  useEffect(() => {
    if (!appUser) {
      const u = new URLSearchParams(window.location.search).get("u");
      if (u) localStorage.setItem("username", u);
    }
    fetchStatus();
  }, [appUser, fetchStatus]); // Dependency array includes fetchStatus (memoized)

  const atLeastOneConnected = status.linkedin?.connected || status.instagram?.connected;
  const goToContent = () => (window.location.href = "/content");

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  return (
    <div className={`login-container ${isDarkMode ? "dark" : "light"}`}>
      {/* Theme Toggle */}
      <button className="theme-toggle top-left" onClick={toggleTheme}>
        {isDarkMode ? "‚òÄÔ∏è Light" : "üåô Dark"}
      </button>

      {/* Logout Button */}
      <button className="logout-button top-right" onClick={handleLogout}>
        üö™ Logout
      </button>

      {/* Floating Icons */}
      <div className="extra-icons">
        <span className="icon1">üöÄ</span>
        <span className="icon2">ü§ñ</span>
        <span className="icon3">üìä</span>
        <span className="icon4">üí°</span>
      </div>

      <div className="login-box" style={{ maxWidth: 720 }}>
        <img src="/logo1922.png" alt="Logo" className="login-logo" />
        <h1 className="login-header">Connect Your Social Accounts</h1>
        <p className="login-subheader">Link your profiles for seamless auto-posting</p>

        {err && (
          <div className="response-error" style={{ marginTop: 10 }}>
            <div className="response-icon">!</div>
            <div className="response-content">
              <p>{err}</p>
            </div>
          </div>
        )}

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: 16,
            marginTop: 16,
          }}
        >
          <div
            className={`platform-card ${status.linkedin.connected ? "selected" : ""}`}
            style={{ padding: 16 }}
          >
            <div className="platform-content">
              <span className="platform-name">LinkedIn</span>
              <p style={{ opacity: 0.9, marginTop: 6, fontSize: 14 }}>
                {status.linkedin.connected
                  ? `Connected (${
                      status.linkedin.preferred_posting_urn?.includes("organization")
                        ? "Organization"
                        : "Personal"
                    })`
                  : "Not connected"}
              </p>
            </div>
            <LinkedInConnect
              appUser={appUser}
              onConnected={fetchStatus}
              connected={status.linkedin.connected}
            />
          </div>

          <div
            className={`platform-card ${status.instagram.connected ? "selected" : ""}`}
            style={{ padding: 16 }}
          >
            <div className="platform-content">
              <span className="platform-name">Instagram</span>
              <p style={{ opacity: 0.9, marginTop: 6, fontSize: 14 }}>
                {status.instagram.connected
                  ? "Connected (Business/Creator)"
                  : "Not connected"}
              </p>
            </div>
            <InstagramConnect
              appUser={appUser}
              onConnected={fetchStatus}
              connected={status.instagram.connected}
            />
          </div>
        </div>

        <div className="form-actions" style={{ marginTop: 24 }}>
          <button
            className="post-button"
            onClick={goToContent}
            disabled={!atLeastOneConnected || loading}
            title={
              atLeastOneConnected
                ? "Continue to content"
                : "Connect at least one account"
            }
          >
            Continue to Content
          </button>
          <button
            className="post-button"
            onClick={goToContent}
            title="Go to Content Creation page"
          >
            Create Content
          </button>
          <button className="reset-button" type="button" onClick={fetchStatus}>
            Refresh Status
          </button>
        </div>

        <p style={{ marginTop: 10, fontSize: 12, opacity: 0.8 }}>
          Tip: If your LinkedIn has a Page where you‚Äôre an admin, we‚Äôll auto-select
          the org URN; else we use your personal URN.
        </p>
      </div>
    </div>
  );
}
