// src/components/LinkedInConnect.js
import React, { useEffect, useState } from 'react';

const LinkedInConnect = ({ appUser, onConnected, connected, connectionDetails = null }) => {
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState(null);

  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data.type === 'linkedin_callback') {
        setIsConnecting(false);
        
        if (event.data.success) {
          const { 
            posting_method, 
            organization_count, 
            has_org_access, 
            person_urn, 
            org_urn, 
            message 
          } = event.data;
          
          setConnectionStatus({
            success: true,
            posting_method,
            organization_count: organization_count || 0,
            has_org_access: has_org_access || false,
            person_urn,
            org_urn,
            message: message || 'Connected successfully'
          });

          let successMessage = `LinkedIn connected successfully!\n`;
          successMessage += `Posting Method: ${posting_method}\n`;
          
          if (has_org_access && organization_count > 0) {
            successMessage += `Organizations Found: ${organization_count}\n`;
            successMessage += `Company Pages Detected: ${organization_count > 0 ? 'Yes' : 'No'}`;
          } else {
            successMessage += `Will post to: Personal Profile\n`;
            successMessage += `Company Pages: None found or no access`;
          }
          
          alert(successMessage);
          onConnected();
          
        } else {
          setConnectionStatus({
            success: false,
            error: event.data.error
          });
          alert(`LinkedIn connection failed: ${event.data.error}`);
        }
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, [onConnected]);

  const handleConnect = () => {
    setIsConnecting(true);
    setConnectionStatus(null);
    
    const clientId = '86geycovoa141y';
    const redirectUri = encodeURIComponent('http://13.233.45.167:5000/social/linkedin/callback');
    
    // FIXED: Only use scopes that your LinkedIn app actually has
    const scopes = [
      'w_member_social',              // Post to personal profile
      'r_organization_social',        // Read org data
      'w_organization_social',        // Post to org pages
      'rw_organization_admin',        // Manage org pages
      'r_organization_followers',     // Org follower data
      'r_organization_social_feed',   // Read org feed
      'w_organization_social_feed',   // Write to org feed
      'r_member_profileAnalytics',    // Profile analytics
      'r_member_postAnalytics',       // Post analytics
      'r_1st_connections_size'        // Connection count
    ].join(' ');
    
    const scope = encodeURIComponent(scopes);
    const state = encodeURIComponent(appUser);
    
    const url = `https://www.linkedin.com/oauth/v2/authorization?response_type=code&client_id=${clientId}&redirect_uri=${redirectUri}&state=${state}&scope=${scope}`;
    
    console.log('LinkedIn Auth URL:', url);
    console.log('Requesting scopes:', scopes);
    
    const popup = window.open(url, 'linkedin-auth', 'width=600,height=700,scrollbars=yes,resizable=yes');
    
    if (!popup) {
      alert('Popup blocked. Please allow popups for this site and try again.');
      setIsConnecting(false);
      return;
    }
    
    const checkClosed = setInterval(() => {
      if (popup.closed) {
        clearInterval(checkClosed);
        setIsConnecting(false);
        setTimeout(() => {
          onConnected();
        }, 1000);
      }
    }, 1000);
  };

  const handleDisconnect = async () => {
    try {
      setIsConnecting(true);
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please login again to disconnect LinkedIn.');
        window.location.href = '/login';
        return;
      }

      const response = await fetch('http://13.233.45.167:5000/social/linkedin/disconnect', {
        method: 'POST',
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ app_user: appUser })
      });
      
      if (response.status === 401) {
        const errorData = await response.json();
        if (errorData.token_expired) {
          localStorage.removeItem('token');
          alert('Your session has expired. Please login again.');
          window.location.href = '/login';
          return;
        }
      }
      
      if (response.ok) {
        setConnectionStatus(null);
        onConnected();
        alert('LinkedIn disconnected successfully!');
      } else {
        const error = await response.json();
        alert(`Failed to disconnect: ${error.error || error.message || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error disconnecting LinkedIn:', error);
      alert('Error disconnecting LinkedIn. Please try again.');
    } finally {
      setIsConnecting(false);
    }
  };

  const getConnectionInfo = () => {
    if (!connected || !connectionDetails) return null;

    const { detail } = connectionDetails;
    if (!detail) return null;

    const { posting_method, has_org_access, organization_count = 0 } = detail;
    
    return (
      <div style={{ marginTop: '8px', fontSize: '12px', color: '#666' }}>
        <div><strong>Method:</strong> {posting_method || 'Personal Profile'}</div>
        {has_org_access && organization_count > 0 && (
          <>
            <div><strong>Organizations:</strong> {organization_count}</div>
            <div><strong>Company Access:</strong> Yes</div>
          </>
        )}
        {(!has_org_access || organization_count === 0) && (
          <div><strong>Company Access:</strong> Personal only</div>
        )}
      </div>
    );
  };

  const getButtonText = () => {
    if (isConnecting) {
      return connected ? 'Disconnecting...' : 'Connecting...';
    }
    return connected ? 'Disconnect LinkedIn' : 'Connect LinkedIn';
  };

  const getButtonStyle = () => {
    const baseStyle = {
      color: 'white',
      border: 'none',
      padding: '10px 16px',
      borderRadius: '6px',
      cursor: isConnecting ? 'not-allowed' : 'pointer',
      fontSize: '14px',
      fontWeight: '500',
      transition: 'all 0.2s ease',
      opacity: isConnecting ? 0.7 : 1
    };

    if (connected) {
      return {
        ...baseStyle,
        backgroundColor: '#dc3545'
      };
    } else {
      return {
        ...baseStyle,
        backgroundColor: '#0077b5'
      };
    }
  };

  return (
    <div>
      <button 
        className="connect-button" 
        onClick={connected ? handleDisconnect : handleConnect}
        disabled={isConnecting}
        style={getButtonStyle()}
      >
        {getButtonText()}
      </button>
      
      {getConnectionInfo()}
      
      {connectionStatus && (
        <div style={{ 
          marginTop: '8px', 
          padding: '8px', 
          borderRadius: '4px',
          fontSize: '12px',
          backgroundColor: connectionStatus.success ? '#d4edda' : '#f8d7da',
          color: connectionStatus.success ? '#155724' : '#721c24',
          border: `1px solid ${connectionStatus.success ? '#c3e6cb' : '#f5c6cb'}`
        }}>
          {connectionStatus.success ? (
            <div>
              <strong>Connected Successfully!</strong><br/>
              {connectionStatus.message}<br/>
              {connectionStatus.has_org_access && connectionStatus.organization_count > 0 && (
                <span>Company Pages: {connectionStatus.organization_count} detected</span>
              )}
            </div>
          ) : (
            <div>
              <strong>Connection Failed</strong><br/>
              {connectionStatus.error}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default LinkedInConnect;
