import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [showPassword, setShowPassword] = useState(false);

  // Enhanced marketing content with environmental focus
  const marketingSlides = [
    {
      title: "Eco-Friendly Digital Marketing",
      subtitle: "Reduce your carbon footprint while growing your business",
      description: "Our AI-powered platform helps you create more effective campaigns with fewer resources, contributing to a greener digital ecosystem.",
      icon: "üå±",
      stats: "50% Less Resource Usage"
    },
    {
      title: "Smart Content Generation",
      subtitle: "AI that works smarter, not harder",
      description: "Generate high-quality content that resonates with your audience while minimizing computational waste through optimized algorithms.",
      icon: "üß†",
      stats: "10x Faster Creation"
    },
    {
      title: "Sustainable Growth Analytics",
      subtitle: "Data-driven decisions for long-term success",
      description: "Track your impact and growth with comprehensive analytics that help you build sustainable marketing strategies.",
      icon: "üìä",
      stats: "360¬∞ Business Insights"
    },
    {
      title: "Community-Driven Innovation",
      subtitle: "Join a movement of conscious marketers",
      description: "Connect with like-minded businesses committed to responsible marketing practices and sustainable growth.",
      icon: "ü§ù",
      stats: "10,000+ Green Marketers"
    }
  ];

  // Auto-rotate slides every 5 seconds
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % marketingSlides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  // Load saved theme
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark") setIsDarkMode(true);
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password }
      );
      const { token, user } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);
      setSuccess("Login successful! Welcome to your eco-friendly dashboard...");
      setTimeout(() => {
        onLogin(user);
        setIsLoading(false);
        window.location.href = "/connect";
      }, 2000);
    } catch (err) {
      setError("Invalid credentials. Please check your username and password.");
      setIsLoading(false);
    }
  };

  return (
    <div className={`enhanced-login-wrapper ${isDarkMode ? "dark-theme" : "light-theme"}`}>
      {/* Animated Background Elements */}
      <div className="background-animation">
        <div className="floating-elements">
          {[...Array(12)].map((_, i) => (
            <div key={i} className={`floating-element element-${i + 1}`}></div>
          ))}
        </div>
        <div className="gradient-orbs">
          <div className="orb orb-1"></div>
          <div className="orb orb-2"></div>
          <div className="orb orb-3"></div>
        </div>
      </div>

      {/* Header Controls */}
      <div className="header-controls">
        <button className="theme-toggle-btn" onClick={toggleTheme}>
          <div className="toggle-track">
            <div className="toggle-thumb">
              <span className="toggle-icon">{isDarkMode ? "‚òÄÔ∏è" : "üåô"}</span>
            </div>
          </div>
        </button>
        <div className="eco-badge">
          <span className="eco-icon">üåç</span>
          <span className="eco-text">Eco-Friendly Platform</span>
        </div>
      </div>

      {/* Main Content Container */}
      <div className="login-main-container">
        {/* Left Side - Marketing Showcase */}
        <div className="marketing-showcase">
          <div className="brand-section">
            <div className="logo-wrapper">
              <img src="/logo1922.png" alt="CraftingBrain" className="brand-logo" />
              <div className="brand-identity">
                <h1 className="brand-name">CraftingBrain</h1>
                <p className="brand-tagline">Sustainable Marketing Solutions</p>
              </div>
            </div>
          </div>

          <div className="hero-content">
            <h2 className="hero-title">
              Build Your Brand
              <span className="highlight-gradient"> Responsibly</span>
            </h2>
            <p className="hero-description">
              Join thousands of forward-thinking businesses using our eco-conscious 
              marketing platform to grow sustainably while making a positive impact.
            </p>
          </div>

          {/* Interactive Feature Slider */}
          <div className="feature-slider">
            <div className="slide-viewport">
              <div className="slide-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                {marketingSlides.map((slide, index) => (
                  <div key={index} className="feature-slide">
                    <div className="slide-icon-wrapper">
                      <span className="slide-icon">{slide.icon}</span>
                    </div>
                    <div className="slide-content">
                      <h3 className="slide-title">{slide.title}</h3>
                      <h4 className="slide-subtitle">{slide.subtitle}</h4>
                      <p className="slide-description">{slide.description}</p>
                      <div className="slide-stat">{slide.stats}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            
            {/* Slide Navigation */}
            <div className="slide-navigation">
              {marketingSlides.map((_, index) => (
                <button
                  key={index}
                  className={`nav-dot ${index === currentSlide ? 'active' : ''}`}
                  onClick={() => setCurrentSlide(index)}
                  aria-label={`Go to slide ${index + 1}`}
                />
              ))}
            </div>
          </div>

          {/* Trust Indicators */}
          <div className="trust-indicators">
            <div className="trust-metric">
              <div className="metric-value">12K+</div>
              <div className="metric-label">Happy Users</div>
            </div>
            <div className="trust-metric">
              <div className="metric-value">2M+</div>
              <div className="metric-label">Posts Created</div>
            </div>
            <div className="trust-metric">
              <div className="metric-value">98%</div>
              <div className="metric-label">Satisfaction</div>
            </div>
          </div>
        </div>

        {/* Right Side - Login Form */}
        <div className="login-form-wrapper">
          <div className="form-container">
            <div className="form-header">
              <h2 className="form-title">Welcome Back</h2>
              <p className="form-subtitle">Sign in to continue your sustainable marketing journey</p>
            </div>

            <form onSubmit={handleSubmit} className="login-form">
              <div className="input-group">
                <label htmlFor="username" className="input-label">Username</label>
                <div className="input-wrapper">
                  <input
                    id="username"
                    type="text"
                    placeholder="Enter your username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="form-input"
                    required
                  />
                  <div className="input-icon">
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
                      <circle cx="12" cy="7" r="4"/>
                    </svg>
                  </div>
                </div>
              </div>

              <div className="input-group">
                <label htmlFor="password" className="input-label">Password</label>
                <div className="input-wrapper">
                  <input
                    id="password"
                    type={showPassword ? "text" : "password"}
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="form-input"
                    required
                  />
                  <div className="input-icon">
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
                      <circle cx="12" cy="16" r="1"/>
                      <path d="M7 11V7a5 5 0 0 1 10 0v4"/>
                    </svg>
                  </div>
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
                  </button>
                </div>
              </div>

              <div className="form-options">
                <label className="checkbox-wrapper">
                  <input type="checkbox" className="checkbox-input" />
                  <span className="checkbox-mark"></span>
                  <span className="checkbox-label">Remember me</span>
                </label>
                <a href="/forgot-password" className="forgot-link">Forgot Password?</a>
              </div>

              {error && (
                <div className="alert alert-error">
                  <div className="alert-icon">‚ö†Ô∏è</div>
                  <div className="alert-content">
                    <strong>Error:</strong> {error}
                  </div>
                </div>
              )}
              
              {success && (
                <div className="alert alert-success">
                  <div className="alert-icon">‚úÖ</div>
                  <div className="alert-content">
                    <strong>Success:</strong> {success}
                  </div>
                </div>
              )}

              <button type="submit" className="submit-btn" disabled={isLoading}>
                {isLoading ? (
                  <div className="btn-loading">
                    <div className="spinner"></div>
                    <span>Signing you in...</span>
                  </div>
                ) : (
                  <div className="btn-content">
                    <span>Sign In</span>
                    <svg className="btn-arrow" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M5 12h14M12 5l7 7-7 7"/>
                    </svg>
                  </div>
                )}
              </button>

              <div className="divider">
                <span className="divider-text">or continue with</span>
              </div>

              <div className="social-login">
                <button type="button" className="social-btn google-btn">
                  <svg className="social-icon" width="20" height="20" viewBox="0 0 24 24">
                    <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                    <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                    <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                    <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                  </svg>
                  <span>Google</span>
                </button>
                <button type="button" className="social-btn linkedin-btn">
                  <svg className="social-icon" width="20" height="20" viewBox="0 0 24 24" fill="#0077B5">
                    <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
                  </svg>
                  <span>LinkedIn</span>
                </button>
              </div>

              <div className="form-footer">
                <p className="signup-prompt">
                  New to our platform?{" "}
                  <a href="/register" className="signup-link">Create an account</a>
                </p>
                <div className="help-links">
                  <a href="/help" className="help-link">Need Help?</a>
                  <a href="/privacy" className="help-link">Privacy Policy</a>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
