// src/components/LinkedInConnect.js
import React from 'react';

const LinkedInConnect = ({ appUser, onConnected, connected }) => {
  const handleConnect = () => {
    const clientId = '86geycovoa141y'; // From .env or config (don't hardcode in prod)
    const redirectUri = encodeURIComponent('https://your-api-endpoint/social/linkedin/callback'); // Backend callback
    const scope = encodeURIComponent('profile openid email w_member_social r_organization_social w_organization_social r_basicprofile r_fullprofile r_emailaddress w_member_social'); // Adjust as needed
    const state = encodeURIComponent(appUser); // Pass appUser for backend verification
    const url = `https://www.linkedin.com/oauth/v2/authorization?response_type=code&client_id=${clientId}&redirect_uri=${redirectUri}&state=${state}&scope=${scope}`;
    window.location.href = url;
  };

  const handleDisconnect = () => {
    // TODO: Call backend to revoke/delete token from DynamoDB
    fetch('https://your-api-endpoint/social/linkedin/disconnect', {
      method: 'POST',
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      body: JSON.stringify({ app_user: appUser })
    }).then(() => onConnected());
  };

  return (
    <button 
      className="connect-button" 
      onClick={connected ? handleDisconnect : handleConnect}
    >
      {connected ? 'Disconnect' : 'Connect'} LinkedIn
    </button>
  );
};

export default LinkedInConnect;
