// src/components/Login.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "light") setIsDarkMode(false);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);
    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password }
      );
      const { token, user } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);
      setSuccess("Login successful! Redirecting...");
      setTimeout(() => {
        onLogin(user);
        setIsLoading(false);
        window.location.href = "/connect";
      }, 1500);
    } catch (err) {
      setError("Invalid username or password");
      setIsLoading(false);
    }
  };

  // Generate stars for the falling animation
  const generateStars = () => {
    const stars = [];
    for (let i = 1; i <= 25; i++) {
      stars.push(<div key={i} className="star"></div>);
    }
    return stars;
  };

  return (
    <div className={`login-page ${isDarkMode ? "dark" : "light"}`}>
      {/* Falling Stars Animation */}
      <div className="stars-container">
        {generateStars()}
      </div>

      {/* Header Section */}
      <header className="header">
        <div className="brand">
          <img src="/logo1922.png" alt="INIKOLA" className="brand-logo" />
          <div className="brand-text">
            <h1 className="brand-name">INIKOLA</h1>
            <p className="brand-tagline">Presenting</p>
          </div>
        </div>
        <nav className="header-nav">
          <a href="/features">Features</a>
          <a href="/pricing">Pricing</a>
          <a href="/about">About</a>
          <a href="/contact">Contact</a>
        </nav>
        <button 
          className="theme-toggle-btn" 
          onClick={toggleTheme}
          aria-label="Toggle theme"
        >
          {isDarkMode ? "‚òÄÔ∏è" : "üåô"}
        </button>
      </header>

      {/* Main Content */}
      <div className="login-content">
        {/* Main Heading */}
        <div className="main-heading">
          <h2>INDIA'S FAVORITE</h2>
          <h2 className="highlight">MARKETING BOT</h2>
          <h3>for accelerated business growth</h3>
        </div>

        {/* Description */}
        <p className="description">
          Transform your social media presence with AI-powered content generation.
          Join 1000+ businesses automating their marketing success.
        </p>

        {/* Feature Cards */}
        <div className="features-grid">
          <div className="feature-card">
            <div className="feature-icon">üé®</div>
            <div className="feature-info">
              <h4>AI Content Creation</h4>
              <p>Generate stunning visuals instantly</p>
            </div>
          </div>
          <div className="feature-card">
            <div className="feature-icon">üì±</div>
            <div className="feature-info">
              <h4>Multi-Platform Posting</h4>
              <p>Instagram, LinkedIn & more</p>
            </div>
          </div>
          <div className="feature-card">
            <div className="feature-icon">‚ö°</div>
            <div className="feature-info">
              <h4>Smart Automation</h4>
              <p>Schedule & optimize posting times</p>
            </div>
          </div>
        </div>

        {/* Login Card */}
        <div className="login-card">
          <div className="card-title">
            <h3>Welcome Back</h3>
            <p>Sign in to continue to your dashboard</p>
          </div>

          <form onSubmit={handleSubmit} className="login-form">
            <div className="form-group">
              <label>USERNAME</label>
              <input
                type="text"
                placeholder="Enter your username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                autoComplete="username"
              />
            </div>

            <div className="form-group">
              <label>PASSWORD</label>
              <input
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                autoComplete="current-password"
              />
            </div>

            {error && (
              <div className="alert error">
                <span className="icon">‚ö†Ô∏è</span>
                <span>{error}</span>
              </div>
            )}
            
            {success && (
              <div className="alert success">
                <span className="icon">‚úì</span>
                <span>{success}</span>
              </div>
            )}

            <button type="submit" className="submit-button" disabled={isLoading}>
              {isLoading ? <div className="spinner"></div> : "SIGN IN"}
            </button>

            <div className="form-footer">
              <p>Don't have an account? <a href="/register">Sign up</a></p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
