// src/Connect.js
import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import LinkedInConnect from "./components/LinkedInConnect";
import InstagramConnect from "./components/InstagramConnect";
import ".components/Login.css";

// Same style as your ContentCreation hardcoded endpoint
const API_SOCIAL_STATUS = "http://13.233.45.167:5000/social/status";

export default function Connect() {
  const [status, setStatus] = useState({
    linkedin: { connected: false, detail: null },
    instagram: { connected: false, detail: null },
  });
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  const token = localStorage.getItem("token");
  const appUser = useMemo(() => localStorage.getItem("username") || "", []);

  const fetchStatus = async () => {
    try {
      setErr("");
      setLoading(true);
      const res = await axios.get(API_SOCIAL_STATUS, {
        headers: { Authorization: `Bearer ${token}` },
        params: { app_user: appUser },
      });
      setStatus({
        linkedin: res.data.linkedin || { connected: false },
        instagram: res.data.instagram || { connected: false },
      });
    } catch (e) {
      setErr(e?.response?.data?.message || "Could not load connection status.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (!appUser) {
      const u = new URLSearchParams(window.location.search).get("u");
      if (u) localStorage.setItem("username", u);
    }
    fetchStatus();
    // eslint-disable-next-line
  }, []);

  const atLeastOneConnected = status.linkedin?.connected || status.instagram?.connected;
  const goToContent = () => (window.location.href = "/content");

  return (
    <div className={`login-container ${localStorage.getItem('theme') === 'dark' ? 'dark' : 'light'}`}>
      <button
        className="logout-button top-right"
        onClick={() => {
          localStorage.removeItem("token");
          window.location.href = "/login";
        }}
      >
        ðŸšª Logout
      </button>

      <div className="login-box" style={{ maxWidth: 720 }}>
        <img src="/logo1922.png" alt="Logo" className="login-logo" />
        <h1 className="login-header">Connect your accounts</h1>
        <p className="login-subheader">Link social profiles for auto-posting</p>

        {err && (
          <div className="response-error" style={{ marginTop: 10 }}>
            <div className="response-icon">!</div>
            <div className="response-content"><p>{err}</p></div>
          </div>
        )}

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 16, marginTop: 16 }}>
          <div className={`platform-card ${status.linkedin.connected ? "selected" : ""}`} style={{ padding: 16 }}>
            <div className="platform-content">
              <span className="platform-name">LinkedIn</span>
              <p style={{ opacity: 0.9, marginTop: 6, fontSize: 14 }}>
                {status.linkedin.connected
                  ? `Connected (${status.linkedin.preferred_posting_urn?.includes("organization") ? "Organization" : "Personal"})`
                  : "Not connected"}
              </p>
            </div>
            <LinkedInConnect appUser={appUser} onConnected={fetchStatus} />
          </div>

          <div className={`platform-card ${status.instagram.connected ? "selected" : ""}`} style={{ padding: 16 }}>
            <div className="platform-content">
              <span className="platform-name">Instagram</span>
              <p style={{ opacity: 0.9, marginTop: 6, fontSize: 14 }}>
                {status.instagram.connected ? "Connected (Business/Creator)" : "Not connected"}
              </p>
            </div>
            <InstagramConnect appUser={appUser} onConnected={fetchStatus} />
          </div>
        </div>

        <div className="form-actions" style={{ marginTop: 24 }}>
          <button
            className="post-button"
            onClick={goToContent}
            disabled={!atLeastOneConnected || loading}
            title={atLeastOneConnected ? "Continue to content" : "Connect at least one account"}
          >
            Continue to Content
          </button>
          <button className="reset-button" type="button" onClick={fetchStatus}>
            Refresh Status
          </button>
        </div>

        <p style={{ marginTop: 10, fontSize: 12, opacity: 0.8 }}>
          Tip: If your LinkedIn has a Page where youâ€™re an admin, weâ€™ll auto-select the org URN; else we use your personal URN.
        </p>
      </div>
    </div>
  );
}
