import React, { useEffect, useMemo, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import LinkedInConnect from "./components/LinkedInConnect";
import InstagramConnect from "./components/InstagramConnect";
import "./components/Connect.css";

// Hardcoded endpoint for social status
const API_SOCIAL_STATUS = "http://13.233.45.167:5000/social/status";

export default function Connect() {
  const [status, setStatus] = useState({
    linkedin: { connected: false, detail: null },
    instagram: { connected: false, detail: null },
  });
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isLoggingOut, setIsLoggingOut] = useState(false); // Add logout state

  const token = localStorage.getItem("token");
  const appUser = useMemo(() => localStorage.getItem("username") || localStorage.getItem("appUser") || "defaultUser", []);
  const navigate = useNavigate();

  // Check if user is properly logged in
  useEffect(() => {
    console.log("Token:", token);
    console.log("App User:", appUser);
    
    if (!token || token === "null" || token === "undefined") {
      console.log("No valid token found, redirecting to login");
      navigate("/login", { replace: true });
      return;
    }
  }, [token, appUser, navigate]);

  // Load saved theme
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark") setIsDarkMode(true);
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  // Fetch status with enhanced LinkedIn details
  const fetchStatus = useCallback(async () => {
    try {
      setErr("");
      setLoading(true);
      
      const currentToken = localStorage.getItem("token");
      if (!currentToken || currentToken === "null" || currentToken === "undefined") {
        console.log("No valid token during fetch, redirecting to login");
        navigate("/login", { replace: true });
        return;
      }
      
      console.log("Fetching status with token:", currentToken);
      const res = await axios.get(API_SOCIAL_STATUS, {
        headers: { Authorization: `Bearer ${currentToken}` },
        params: { app_user: appUser },
      });
      console.log("API Response:", res.data);
      
      setStatus({
        linkedin: res.data.linkedin || { connected: false, detail: null },
        instagram: res.data.instagram || { connected: false, detail: null },
      });
    } catch (e) {
      console.error("Fetch status error:", e);
      
      if (e.response?.status === 401) {
        console.log("401 error - token expired or invalid, redirecting to login");
        localStorage.clear();
        navigate("/login", { replace: true });
        return;
      }
      
      const errorMessage = e?.response?.data?.message || "Could not load connection status.";
      setErr(errorMessage);
      setStatus({
        linkedin: { connected: false, detail: null },
        instagram: { connected: false, detail: null },
      });
    } finally {
      setLoading(false);
      console.log("Loading state set to false");
    }
  }, [appUser, navigate]);

  useEffect(() => {
    if (!appUser) {
      const u = new URLSearchParams(window.location.search).get("u");
      if (u) localStorage.setItem("username", u);
    }
    
    // Only fetch status if we have a valid token
    if (token && token !== "null" && token !== "undefined") {
      fetchStatus();
    }
  }, [appUser, fetchStatus, token]);

  const goToContent = () => {
    console.log("Button clicked - navigating to content creation");
    
    const currentToken = localStorage.getItem("token");
    console.log("Token check:", !!currentToken);
    
    if (!currentToken || currentToken === "null" || currentToken === "undefined") {
      console.warn("No token found, redirecting to login");
      navigate("/login", { replace: true });
      return;
    }
    
    try {
      console.log("Navigating to /content");
      navigate("/content");
      console.log("Navigation called successfully");
    } catch (error) {
      console.error("Navigation error:", error);
      setErr("Failed to navigate to content page.");
    }
  };

  const handleLogout = useCallback(() => {
    console.log("Logging out...");
    setIsLoggingOut(true); // Set logout state immediately
    
    // Clear localStorage
    localStorage.clear();
    
    // Directly navigate using window.location for immediate redirect
    window.location.href = "/login";
  }, []);

  // Helper function to get LinkedIn connection status text
  const getLinkedInStatusText = () => {
    if (!status.linkedin.connected) return "Not connected";
    
    const detail = status.linkedin.detail;
    if (!detail) return "Connected";
    
    const hasOrg = detail.has_org_access && detail.all_org_urns && detail.all_org_urns.length > 0;
    const orgCount = detail.all_org_urns ? detail.all_org_urns.length : 0;
    
    if (hasOrg) {
      return `Connected (${orgCount} Company Page${orgCount > 1 ? 's' : ''})`;
    } else {
      return "Connected (Personal Profile)";
    }
  };

  // Show logout loading state
  if (isLoggingOut) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        background: '#1a1a2e'
      }}>
        <div style={{ color: 'white', fontSize: '18px' }}>
          Logging out...
        </div>
      </div>
    );
  }

  // Don't render anything if no token
  if (!token || token === "null" || token === "undefined") {
    return null; // Return null instead of showing message
  }

  return (
    <div className={`login-container ${isDarkMode ? "dark" : "light"}`}>
      {/* Theme Toggle */}
      <button className="theme-toggle top-left" onClick={toggleTheme}>
        {isDarkMode ? "‚òÄÔ∏è Light" : "üåô Dark"}
      </button>

      {/* Logout Button */}
      <button 
        className="logout-button top-right" 
        onClick={handleLogout}
        style={{ cursor: 'pointer' }}
      >
        üö™ Logout
      </button>

      {/* Floating Icons */}
      <div className="extra-icons">
        <span className="icon1">üöÄ</span>
        <span className="icon2">ü§ñ</span>
        <span className="icon3">üìä</span>
        <span className="icon4">üí°</span>
      </div>

      <div className="login-box" style={{ maxWidth: 720 }}>
        <img src="/logo1922.png" alt="Logo" className="login-logo" />
        <h1 className="login-header">Connect Your Social Accounts</h1>
        <p className="login-subheader">Link your profiles for seamless auto-posting</p>

        {err && (
          <div className="response-error" style={{ marginTop: 10 }}>
            <div className="response-icon">!</div>
            <div className="response-content">
              <p>{err}</p>
            </div>
          </div>
        )}

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: 16,
            marginTop: 16,
          }}
        >
          {/* LinkedIn Connection Card */}
          <div
            className={`platform-card ${status.linkedin.connected ? "selected" : ""}`}
            style={{ 
              padding: 16,
              border: status.linkedin.connected ? '2px solid #0077b5' : '1px solid #333'
            }}
          >
            <div className="platform-content">
              <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                <span className="platform-name">LinkedIn</span>
                {status.linkedin.connected && (
                  <span style={{ 
                    fontSize: '12px', 
                    padding: '2px 6px', 
                    borderRadius: '3px', 
                    backgroundColor: '#0077b5', 
                    color: 'white' 
                  }}>
                    ‚úì CONNECTED
                  </span>
                )}
              </div>
              
              <p style={{ opacity: 0.9, marginTop: 6, fontSize: 14 }}>
                {status.linkedin.connected ? getLinkedInStatusText() : ""}
              </p>

              {/* Enhanced LinkedIn Connection Details */}
              {status.linkedin.connected && status.linkedin.detail && (
                <div style={{ 
                  marginTop: 8, 
                  padding: 8, 
                  backgroundColor: 'rgba(0, 119, 181, 0.1)', 
                  borderRadius: 4,
                  fontSize: 12
                }}>
                  <div><strong>Posting Method:</strong> {status.linkedin.detail.posting_method || 'Personal Profile'}</div>
                  
                  {status.linkedin.detail.has_org_access && status.linkedin.detail.all_org_urns && status.linkedin.detail.all_org_urns.length > 0 && (
                    <>
                      <div><strong>Company Pages:</strong> {status.linkedin.detail.all_org_urns.length} detected</div>
                      <div style={{ color: '#0077b5' }}>‚úì Organization posting enabled</div>
                    </>
                  )}
                  
                  {(!status.linkedin.detail.has_org_access || !status.linkedin.detail.all_org_urns || status.linkedin.detail.all_org_urns.length === 0) && (
                    <div style={{ color: '#888' }}>‚Ä¢ Personal profile posting only</div>
                  )}
                  
                  <div style={{ marginTop: 4, fontSize: 11, opacity: 0.7 }}>
                    Connected: {status.linkedin.detail.connected_at ? new Date(status.linkedin.detail.connected_at).toLocaleDateString() : 'Recently'}
                  </div>
                </div>
              )}
            </div>
            
            <LinkedInConnect
              appUser={appUser}
              onConnected={fetchStatus}
              connected={status.linkedin.connected}
              connectionDetails={status.linkedin}
            />
          </div>

          {/* Instagram Connection Card */}
          <div
            className={`platform-card ${status.instagram.connected ? "selected" : ""}`}
            style={{ padding: 16 }}
          >
            <div className="platform-content">
              <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                <span className="platform-name">Instagram</span>
                {status.instagram.connected && (
                  <span style={{ 
                    fontSize: '12px', 
                    padding: '2px 6px', 
                    borderRadius: '3px', 
                    backgroundColor: '#E4405F', 
                    color: 'white' 
                  }}>
                    ‚úì CONNECTED
                  </span>
                )}
              </div>
              
              <p style={{ opacity: 0.9, marginTop: 6, fontSize: 14 }}>
                {status.instagram.connected
                  ? "Connected (Business/Creator)"
                  : ""}
              </p>

              {status.instagram.connected && status.instagram.detail && (
                <div style={{ 
                  marginTop: 8, 
                  padding: 8, 
                  backgroundColor: 'rgba(228, 64, 95, 0.1)', 
                  borderRadius: 4,
                  fontSize: 12
                }}>
                  <div><strong>Account ID:</strong> {status.instagram.detail.user_id}</div>
                  <div style={{ marginTop: 4, fontSize: 11, opacity: 0.7 }}>
                    Connected: {status.instagram.detail.connected_at ? new Date(status.instagram.detail.connected_at).toLocaleDateString() : 'Recently'}
                  </div>
                </div>
              )}
            </div>
            
            <InstagramConnect
              appUser={appUser}
              onConnected={fetchStatus}
              connected={status.instagram.connected}
            />
          </div>
        </div>

        <div className="form-actions" style={{ marginTop: 24 }}>
          <button
            className="post-button"
            onClick={goToContent}
            disabled={loading}
            title="Continue to content"
          >
            Continue to Content
          </button>
          <button className="reset-button" type="button" onClick={fetchStatus}>
            Refresh Status
          </button>
        </div>

        {/* Enhanced Tips Section */}
        <div style={{ marginTop: 16, padding: 12, backgroundColor: 'rgba(255, 193, 7, 0.1)', borderRadius: 6, fontSize: 12 }}>
          <div style={{ fontWeight: 'bold', marginBottom: 6 }}>Connection Tips:</div>
          <div>‚Ä¢ LinkedIn: If you're an admin of company pages, we'll detect and enable organization posting</div>
          <div>‚Ä¢ Personal profiles work for all users - no admin access needed</div>
          <div>‚Ä¢ Instagram: Requires Business or Creator account for API access</div>
        </div>

        {/* Debug info */}
        <div style={{ marginTop: 20, fontSize: 10, opacity: 0.5 }}>
          Debug: User: {appUser} | LinkedIn: {status.linkedin.connected ? "Connected" : "Not connected"} 
          {status.linkedin.connected && status.linkedin.detail?.all_org_urns && (
            ` | Orgs: ${status.linkedin.detail.all_org_urns.length}`
          )}
        </div>
      </div>
    </div>
  );
}
