// src/Connect.js (Updated)
import React, { useEffect, useMemo, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import LinkedInConnect from "./components/LinkedInConnect";
import InstagramConnect from "./components/InstagramConnect";
import "./components/Connect.css";

const API_SOCIAL_STATUS = "http://13.233.45.167:5000/social/status";

export default function Connect() {
  const [status, setStatus] = useState({
    linkedin: { connected: false, detail: null },
    instagram: { connected: false, detail: null },
  });
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [isDarkMode, setIsDarkMode] = useState(false);

  const token = localStorage.getItem("token");
  const appUser = useMemo(() => localStorage.getItem("username") || localStorage.getItem("appUser") || "defaultUser", []);
  const navigate = useNavigate();

  useEffect(() => {
    if (!token || token === "null" || token === "undefined") {
      navigate("/login", { replace: true });
      return;
    }
  }, [token, appUser, navigate]);

  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark") setIsDarkMode(true);
  }, []);

  const fetchStatus = useCallback(async () => {
    try {
      setErr("");
      setLoading(true);
      
      const currentToken = localStorage.getItem("token");
      if (!currentToken || currentToken === "null" || currentToken === "undefined") {
        navigate("/login", { replace: true });
        return;
      }
      
      const res = await axios.get(API_SOCIAL_STATUS, {
        headers: { Authorization: `Bearer ${currentToken}` },
        params: { app_user: appUser },
      });
      
      setStatus({
        linkedin: res.data.linkedin || { connected: false, detail: null },
        instagram: res.data.instagram || { connected: false, detail: null },
      });
    } catch (e) {
      if (e.response?.status === 401) {
        localStorage.clear();
        navigate("/login", { replace: true });
        return;
      }
      
      const errorMessage = e?.response?.data?.message || "Could not load connection status.";
      setErr(errorMessage);
      setStatus({
        linkedin: { connected: false, detail: null },
        instagram: { connected: false, detail: null },
      });
    } finally {
      setLoading(false);
    }
  }, [appUser, navigate]);

  useEffect(() => {
    if (!appUser) {
      const u = new URLSearchParams(window.location.search).get("u");
      if (u) localStorage.setItem("username", u);
    }
    
    if (token && token !== "null" && token !== "undefined") {
      fetchStatus();
    }
  }, [appUser, fetchStatus, token]);

  const goToContent = () => {
    const currentToken = localStorage.getItem("token");
    
    if (!currentToken || currentToken === "null" || currentToken === "undefined") {
      navigate("/login", { replace: true });
      return;
    }
    
    try {
      navigate("/content");
    } catch (error) {
      setErr("Failed to navigate to content page.");
    }
  };

  if (!token || token === "null" || token === "undefined") {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        background: '#1a1a2e'
      }}>
        <div style={{ color: 'white', fontSize: '18px' }}>
          Redirecting to login...
        </div>
      </div>
    );
  }

  return (
    <div className={`connect-container ${isDarkMode ? "dark" : "light"}`}>
      <div className="floating-elements">
        <span className="floating-element">🚀</span>
        <span className="floating-element">🤖</span>
        <span className="floating-element">📊</span>
        <span className="floating-element">💡</span>
      </div>

      <div className="connect-box">
        <div className="connect-header">
          <img src="/logo1922.png" alt="Logo" className="login-logo" />
          <h1>Connect Your Social Accounts</h1>
          <p>Link your profiles for seamless auto-posting</p>
        </div>

        {err && (
          <div className="response-error" style={{ marginBottom: '1.5rem' }}>
            <div className="response-icon">!</div>
            <div className="response-content">
              <p>{err}</p>
            </div>
          </div>
        )}

        <div className="platforms-grid">
          <div className={`platform-connect-card ${status.linkedin.connected ? "connected" : ""}`}>
            <div className="platform-header">
              <div className="platform-icon linkedin">
                in
              </div>
              <div className="platform-details">
                <h3>LinkedIn</h3>
                <p>
                  {status.linkedin.connected
                    ? `Connected (${
                        status.linkedin.detail?.preferred_posting_urn?.includes("organization")
                          ? "Organization"
                          : "Personal"
                      })`
                    : "Not connected"}
                </p>
              </div>
            </div>
            <LinkedInConnect
              appUser={appUser}
              onConnected={fetchStatus}
              connected={status.linkedin.connected}
            />
          </div>

          <div className={`platform-connect-card ${status.instagram.connected ? "connected" : ""}`}>
            <div className="platform-header">
              <div className="platform-icon instagram">
                📷
              </div>
              <div className="platform-details">
                <h3>Instagram</h3>
                <p>
                  {status.instagram.connected
                    ? "Connected (Business/Creator)"
                    : "Not connected"}
                </p>
              </div>
            </div>
            <InstagramConnect
              appUser={appUser}
              onConnected={fetchStatus}
              connected={status.instagram.connected}
            />
          </div>
        </div>

        <div className="connect-actions">
          <button
            className="continue-button"
            onClick={goToContent}
            disabled={loading}
            title="Continue to content"
          >
            Continue to Content
          </button>
          <button className="refresh-button" type="button" onClick={fetchStatus}>
            Refresh Status
          </button>
        </div>

        <p style={{ 
          marginTop: '1rem', 
          fontSize: '12px', 
          opacity: 0.8, 
          textAlign: 'center', 
          color: 'var(--text-secondary)' 
        }}>
          Tip: If your LinkedIn has a Page where you're an admin, we'll auto-select
          the org URN; else we use your personal URN.
        </p>

        <div className="debug-info">
          Debug: Token exists: {!!token}, AppUser: {appUser}, LinkedIn: {status.linkedin.connected ? "Connected" : "Not connected"}
        </div>
      </div>
    </div>
  );
}
