// 📁 src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Header from "./components/Header";
import Login from "./components/Login";
import ContentCreation from "./components/ContentCreation";
import Register from "./components/Register";
import Survey from "./components/Survey";
import Connect from "./Connect";
import './App.css';

const App = () => {
  const [user, setUser] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const savedTheme = localStorage.getItem("theme");
    
    if (token) {
      setUser({ username: localStorage.getItem("username") || "admin" });
    }
    
    if (savedTheme === "dark") {
      setIsDarkMode(true);
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    localStorage.removeItem("appUser");
    setUser(null);
  };

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  return (
    <div className={`app ${isDarkMode ? 'dark' : 'light'}`}>
      <Router>
        <Header 
          user={user} 
          onLogout={handleLogout} 
          isDarkMode={isDarkMode} 
          onToggleTheme={toggleTheme}
        />
        <main className="main-content">
          <Routes>
            <Route
              path="/login"
              element={
                user ? <Navigate to="/connect" /> : <Login onLogin={handleLogin} />
              }
            />
            <Route
              path="/register"
              element={user ? <Navigate to="/connect" /> : <Register />}
            />
            <Route path="/survey" element={<Survey />} />
            <Route
              path="/connect"
              element={user ? <Connect /> : <Navigate to="/login" />}
            />
            <Route
              path="/content"
              element={user ? <ContentCreation user={user} /> : <Navigate to="/login" />}
            />
            <Route path="*" element={<Navigate to="/login" />} />
          </Routes>
        </main>
      </Router>
    </div>
  );
};

export default App;
